import React, { useState } from 'react';
import axios from 'axios';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5050/api/auth/login', {
        username,
        password
      });

      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));

      const role = res.data.user.role;
      if (role === 'director') {
        window.location.href = '/dashboard/director';
      } else if (role === 'inventoryManager') {
        window.location.href = '/dashboard/inventory';
      } else if (role === 'stembassador') {
        window.location.href = '/dashboard/stembassador';
      } else {
        setError('Unknown role. Contact admin.');
      }
    } catch (err) {
      setError('Login failed. Please check your credentials.');
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h1 style={styles.title}>üîê Project Exploration</h1>
        <p style={styles.subtitle}>Sign in to access the inventory portal</p>

        <form onSubmit={handleLogin}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            style={styles.input}
            required
          />
          <div style={styles.passwordWrapper}>
            <input
              type={showPassword ? 'text' : 'password'}
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={styles.input}
              required
            />
            <span
              onClick={() => setShowPassword(!showPassword)}
              style={styles.toggle}
            >
              {showPassword ? 'Hide' : 'Show'}
            </span>
          </div>

          <div style={styles.linkContainer}>
            <a href="#" style={styles.link}>Forgot Password?</a>
          </div>

          <button type="submit" style={styles.button}>Login</button>

          {error && <p style={styles.error}>{error}</p>}
        </form>
      </div>
    </div>
  );
};

const styles = {
  container: {
    height: '100vh',
    background: 'linear-gradient(to right, #a1c4fd, #c2e9fb)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontFamily: 'Arial, sans-serif'
  },
  card: {
    backgroundColor: '#fff',
    padding: '40px 30px',
    borderRadius: '12px',
    boxShadow: '0 8px 24px rgba(0, 0, 0, 0.1)',
    width: '350px',
    textAlign: 'center'
  },
  title: {
    fontSize: '24px',
    marginBottom: '8px',
    fontWeight: 'bold'
  },
  subtitle: {
    fontSize: '14px',
    color: '#666',
    marginBottom: '20px'
  },
  input: {
    width: '100%',
    padding: '12px',
    marginBottom: '12px',
    borderRadius: '6px',
    border: '1px solid #ccc',
    fontSize: '14px',
    outline: 'none'
  },
  passwordWrapper: {
    position: 'relative'
  },
  toggle: {
    position: 'absolute',
    top: '50%',
    right: '10px',
    transform: 'translateY(-50%)',
    fontSize: '12px',
    color: '#007bff',
    cursor: 'pointer'
  },
  linkContainer: {
    textAlign: 'right',
    marginBottom: '10px'
  },
  link: {
    fontSize: '12px',
    color: '#007bff',
    textDecoration: 'none'
  },
  button: {
    width: '100%',
    padding: '12px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '6px',
    fontWeight: 'bold',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease'
  },
  error: {
    color: 'red',
    marginTop: '10px',
    fontSize: '13px'
  }
};

export default LoginPage;
