import React from 'react';
import { useNavigate } from 'react-router-dom';

const InventoryManagerDashboard = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));

  return (
    <div className="p-10 min-h-screen" style={{ background: 'linear-gradient(to bottom right, #e0ffe0, #f0fff0)' }}>
      <div className="bg-white p-6 rounded-lg shadow-md mb-8">
        <h1 className="text-2xl font-bold mb-2">Welcome, {user?.username || 'Inventory Manager'}!</h1>
        <p className="text-gray-700">Role: {user?.role}</p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold mb-2">ðŸ“¦ Manage Inventory</h2>
          <p className="text-gray-600 mb-4">Add, edit, or remove stock items.</p>
          <button onClick={() => navigate('/inventory')} className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
            Go
          </button>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold mb-2">ðŸ“¥ Fulfill Requests</h2>
          <p className="text-gray-600 mb-4">View and process item requests submitted by STEMbassadors.</p>
          <button onClick={() => navigate('/request')} className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
            Go
          </button>
        </div>
      </div>
    </div>
  );
};

export default InventoryManagerDashboard;
