import React from 'react';
import { useNavigate } from 'react-router-dom';

const DirectorDashboard = () => {
  const user = JSON.parse(localStorage.getItem('user'));
  const navigate = useNavigate();

  return (
    <div style={styles.container}>
      <h1>Welcome, {user?.username}!</h1>
      <h3>Role: {user?.role}</h3>

      <div style={styles.grid}>
        <div style={styles.card}>
          <h3>ðŸ“¦ View Inventory</h3>
          <p>Browse and manage items in stock.</p>
          <button onClick={() => navigate('/director/inventory')} style={styles.btn}>
            Go
          </button>
        </div>
        <div style={styles.card}>
          <h3>âœ… Approve Requests</h3>
          <p>Review and manage item requests.</p>
          <button onClick={() => navigate('/director/approve')} style={styles.btn}>
            Go
          </button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    textAlign: 'center',
    marginTop: '40px',
    padding: '20px',
    fontFamily: 'Arial, sans-serif'
  },
  grid: {
    display: 'flex',
    justifyContent: 'center',
    gap: '40px',
    marginTop: '30px',
    flexWrap: 'wrap'
  },
  card: {
    width: '300px',
    padding: '20px',
    borderRadius: '10px',
    backgroundColor: '#f7f7f7',
    boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
    textAlign: 'left'
  },
  btn: {
    marginTop: '10px',
    padding: '8px 16px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer'
  }
};

export default DirectorDashboard;
