import React, { useEffect, useState } from 'react';
import axios from 'axios';
import AddInventoryItemForm from '../components/AddInventoryItemForm';

const InventoryPage = () => {
  const [inventory, setInventory] = useState([]);

  const fetchInventory = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/inventory/all');
      setInventory(res.data);
    } catch (error) {
      console.error('Error fetching inventory:', error);
    }
  };

  useEffect(() => {
    fetchInventory();
  }, []);

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>ðŸ“¦ Inventory Items</h2>

      {/* Add Form */}
      <AddInventoryItemForm onAdd={fetchInventory} />

      {/* Inventory Table */}
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.th}>Item Name</th>
            <th style={styles.th}>Description</th>
            <th style={styles.th}>Location</th>
            <th style={styles.th}>Quantity</th>
          </tr>
        </thead>
        <tbody>
          {inventory.map((item) => (
            <tr key={item._id}>
              <td style={styles.td}>{item.name}</td>
              <td style={styles.td}>{item.description || 'â€”'}</td>
              <td style={styles.td}>{item.location}</td>
              <td style={styles.td}>{item.quantity}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const styles = {
  container: {
    minHeight: '100vh',
    padding: '40px',
    background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
    fontFamily: 'Arial, sans-serif',
    color: '#fff'
  },
  heading: {
    marginBottom: '30px',
    fontSize: '28px',
    fontWeight: 'bold'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    backgroundColor: '#fff',
    color: '#333',
    borderRadius: '12px',
    overflow: 'hidden',
    marginTop: '20px'
  },
  th: {
    backgroundColor: '#007bff',
    color: '#fff',
    padding: '12px',
    textAlign: 'left'
  },
  td: {
    padding: '10px',
    borderBottom: '1px solid #ddd'
  }
};

export default InventoryPage;
