import React from 'react';
import { useNavigate } from 'react-router-dom';

const InventoryManagerDashboard = () => {
  const user = JSON.parse(localStorage.getItem('user'));
  const navigate = useNavigate();

  return (
    <div style={styles.container}>
      {/* PE Logo */}
      <div style={styles.logoWrapper}>
        <img src="/pe-logo.jpeg" alt="Project Exploration Logo" style={styles.logo} />
      </div>

      <div style={styles.header}>
        <h2 style={styles.title}>Welcome, {user?.username || 'Inventory Manager'}!</h2>
        <p style={styles.subtitle}>Role: <strong>{user?.role}</strong></p>
      </div>

      <div style={styles.grid}>
        <div style={styles.card}>
          <h3>ðŸ“¦ Manage Inventory</h3>
          <p>View and update existing stock.</p>
          <button onClick={() => navigate('/inventory')} style={styles.button}>Go</button>
        </div>

        <div style={styles.card}>
          <h3>ðŸ“¬ Fulfill Requests</h3>
          <p>Process item requests from STEMbassadors.</p>
          <button onClick={() => navigate('/approve')} style={styles.button}>Go</button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '40px',
    minHeight: '100vh',
    background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
    fontFamily: 'Segoe UI, sans-serif',
    color: 'white',
  },
  logoWrapper: {
    display: 'flex',
    justifyContent: 'center',
    marginBottom: '20px'
  },
  logo: {
    width: '100px',
    borderRadius: '12px'
  },
  header: {
    textAlign: 'center',
    marginBottom: '40px',
  },
  title: {
    fontSize: '28px',
    fontWeight: 'bold',
  },
  subtitle: {
    fontSize: '16px',
  },
  grid: {
    display: 'flex',
    justifyContent: 'center',
    gap: '30px',
    flexWrap: 'wrap',
  },
  card: {
    width: '280px',
    backgroundColor: '#ffffff',
    color: '#000',
    padding: '25px',
    borderRadius: '16px',
    boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
    textAlign: 'center',
  },
  button: {
    marginTop: '12px',
    padding: '10px 16px',
    backgroundColor: '#0f2027',
    color: '#fff',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    fontWeight: 'bold',
    transition: 'background-color 0.3s ease',
  },
};

export default InventoryManagerDashboard;
