import React, { useEffect, useState } from 'react';
import axios from 'axios';

const InventoryPage = () => {
  const [inventory, setInventory] = useState([]);
  const [viewMode, setViewMode] = useState('update');
  const [showAddForm, setShowAddForm] = useState(false);
  const [newItem, setNewItem] = useState({ name: '', description: '', location: '', count: '' });

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/inventory/');
      setInventory(res.data);
    } catch (err) {
      console.error('Error fetching inventory:', err);
    }
  };

  const groupByRack = (items) => {
    const grouped = {};
    items.forEach(item => {
      const rack = item.location && item.location.trim() !== ''
        ? item.location.toUpperCase()
        : 'Unknown Rack';
      if (!grouped[rack]) grouped[rack] = [];
      grouped[rack].push(item);
    });
    return grouped;
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5050/api/inventory/${id}`);
      setInventory(inventory.filter(item => item._id !== id));
    } catch (err) {
      console.error('Error deleting item:', err);
    }
  };

  const handleAddSubmit = async () => {
    try {
      const res = await axios.post('http://localhost:5050/api/inventory/add', {
        name: newItem.name,
        description: newItem.description,
        location: newItem.location,
        quantity: parseInt(newItem.count || '0') // match backend field
      });

      setInventory([...inventory, res.data.item]);
      setShowAddForm(false);
      setNewItem({ name: '', description: '', location: '', count: '' });
    } catch (err) {
      console.error('Error adding item:', err);
    }
  };

  return (
    <div style={styles.container}>
      <h2>📦 Inventory Items</h2>
      <div style={styles.toggleBtns}>
        <button onClick={() => setViewMode('view')} style={styles.toggleBtn}>View Mode</button>
        <button onClick={() => setViewMode('update')} style={styles.toggleBtn}>Update Mode</button>
      </div>

      {viewMode === 'update' && (
        <button onClick={() => setShowAddForm(true)} style={styles.addBtn}>+ Add New Item</button>
      )}

      {showAddForm && (
        <div style={styles.modal}>
          <h3>Add New Item</h3>
          <input placeholder="Name" value={newItem.name} onChange={(e) => setNewItem({ ...newItem, name: e.target.value })} />
          <input placeholder="Description" value={newItem.description} onChange={(e) => setNewItem({ ...newItem, description: e.target.value })} />
          <input placeholder="Location" value={newItem.location} onChange={(e) => setNewItem({ ...newItem, location: e.target.value })} />
          <input placeholder="Count" type="number" value={newItem.count} onChange={(e) => setNewItem({ ...newItem, count: e.target.value })} />
          <div style={{ marginTop: '10px' }}>
            <button onClick={handleAddSubmit} style={styles.editBtn}>Add</button>
            <button onClick={() => setShowAddForm(false)} style={styles.deleteBtn}>Cancel</button>
          </div>
        </div>
      )}

      {viewMode === 'view' ? (
        Object.keys(groupByRack(inventory)).length === 0 ? (
          <p>No items available to display.</p>
        ) : (
          Object.entries(groupByRack(inventory)).map(([rack, items]) => (
            <div key={rack} style={styles.rackGroup}>
              <h3>📍 Rack {rack}</h3>
              <table style={styles.table}>
                <thead>
                  <tr>
                    <th>Item Name</th>
                    <th>Description</th>
                    <th>Count</th>
                  </tr>
                </thead>
                <tbody>
                  {items.map(item => (
                    <tr key={item._id}>
                      <td>{item.name}</td>
                      <td>{item.description || '—'}</td>
                      <td>{item.count || item.quantity || '—'}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))
        )
      ) : (
        <table style={styles.table}>
          <thead>
            <tr>
              <th>Item Name</th>
              <th>Description</th>
              <th>Location</th>
              <th>Count</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {inventory.map(item => (
              <tr key={item._id}>
                <td>{item.name}</td>
                <td>{item.description || '—'}</td>
                <td>{item.location}</td>
                <td>{item.count || item.quantity || '—'}</td>
                <td>
                  <button style={styles.editBtn}>Edit</button>
                  <button style={styles.deleteBtn} onClick={() => handleDelete(item._id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
    minHeight: '100vh',
    color: 'white'
  },
  toggleBtns: {
    marginBottom: '20px',
    display: 'flex',
    gap: '10px'
  },
  toggleBtn: {
    backgroundColor: '#1e90ff',
    border: 'none',
    color: 'white',
    padding: '8px 16px',
    borderRadius: '6px',
    cursor: 'pointer',
    fontWeight: 'bold'
  },
  addBtn: {
    backgroundColor: 'green',
    color: 'white',
    padding: '10px 16px',
    border: 'none',
    borderRadius: '6px',
    marginBottom: '15px',
    cursor: 'pointer'
  },
  rackGroup: {
    backgroundColor: 'white',
    color: 'black',
    padding: '10px',
    borderRadius: '8px',
    marginBottom: '20px'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    marginTop: '10px',
    backgroundColor: '#fff',
    color: '#000'
  },
  editBtn: {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '6px 10px',
    marginRight: '5px',
    border: 'none',
    borderRadius: '4px'
  },
  deleteBtn: {
    backgroundColor: '#dc3545',
    color: 'white',
    padding: '6px 10px',
    border: 'none',
    borderRadius: '4px'
  },
  modal: {
    backgroundColor: 'white',
    color: 'black',
    padding: '20px',
    borderRadius: '10px',
    marginBottom: '20px',
    maxWidth: '300px'
  }
};

export default InventoryPage;
