import React, { useEffect, useState } from 'react';
import axios from 'axios';

const FulfillRequestsPage = () => {
  const [requests, setRequests] = useState([]);
  const [message, setMessage] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchRequests();
  }, []);

  const fetchRequests = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/requests/all');
      setRequests(res.data);
    } catch (err) {
      console.error('Error fetching requests:', err);
    }
  };

  const handleAction = async (id, action) => {
    try {
      await axios.put(`http://localhost:5050/api/requests/${action}/${id}`);
      setMessage(`Request ${action}ed successfully.`);
      fetchRequests();
    } catch (err) {
      console.error(`Error trying to ${action} request:`, err);
      setMessage(`Failed to ${action} request.`);
    }
  };

  const filteredRequests = requests.filter((req) =>
    (req.itemName || req.itemId?.name || '').toLowerCase().includes(searchTerm.toLowerCase()) ||
    (req.requestedBy || '').toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleExport = () => {
    const csv = filteredRequests
      .map((req) =>
        [
          `"${req.itemName || req.itemId?.name || 'Unknown'}"`,
          req.quantity,
          req.requestedBy,
          req.status || 'pending'
        ].join(',')
      )
      .join('\n');

    const blob = new Blob([`Item,Quantity,Requested By,Status\n${csv}`], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'requests_export.csv';
    a.click();
  };

  // Count status
  const approvedCount = requests.filter(req => req.status === 'approved').length;
  const rejectedCount = requests.filter(req => req.status === 'rejected').length;
  const pendingCount = requests.filter(req => !req.status || req.status === 'pending').length;

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>📬 <span style={{ color: 'white' }}>Fulfill Requests</span></h2>

      {message && <p style={styles.message}>{message}</p>}

      <div style={styles.topControls}>
        <input
          type="text"
          placeholder="Search by item or user..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={styles.searchBar}
        />
        <button onClick={handleExport} style={styles.exportButton}>Export</button>
      </div>

      <div style={styles.summaryBox}>
        <strong>Summary:</strong> &nbsp;
        ✅ Approved: {approvedCount} &nbsp;|&nbsp;
        ❌ Rejected: {rejectedCount} &nbsp;|&nbsp;
        ⏳ Pending: {pendingCount}
      </div>

      <table style={styles.table}>
        <thead>
          <tr style={styles.tableHead}>
            <th>Item</th>
            <th>Quantity</th>
            <th>Requested By</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredRequests.map((req) => (
            <tr key={req._id}>
              <td>{req.itemName || req.itemId?.name || 'Unknown'}</td>
              <td>{req.quantity}</td>
              <td>{req.requestedBy}</td>
              <td>{req.status || 'pending'}</td>
              <td>
                <button
                  onClick={() => handleAction(req._id, 'approve')}
                  style={styles.approveBtn}
                >
                  Approve
                </button>
                <button
                  onClick={() => handleAction(req._id, 'reject')}
                  style={styles.rejectBtn}
                >
                  Reject
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    fontFamily: 'Arial, sans-serif',
    minHeight: '100vh',
    background: 'linear-gradient(to right, #1d2b32, #204454)',
    color: 'white'
  },
  title: {
    color: '#ffffff'
  },
  message: {
    padding: '10px',
    marginBottom: '15px',
    backgroundColor: '#d4edda',
    color: '#155724',
    border: '1px solid #c3e6cb',
    borderRadius: '5px',
    fontWeight: 'bold'
  },
  topControls: {
    display: 'flex',
    justifyContent: 'space-between',
    marginBottom: '15px'
  },
  searchBar: {
    padding: '8px',
    width: '300px',
    borderRadius: '6px',
    border: '1px solid #ccc'
  },
  exportButton: {
    backgroundColor: '#007bff',
    color: '#fff',
    padding: '8px 12px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer'
  },
  summaryBox: {
    backgroundColor: '#e3f2fd',
    padding: '10px',
    marginBottom: '10px',
    borderRadius: '5px',
    color: '#000'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    backgroundColor: '#fff',
    color: '#000',
    boxShadow: '0 0 8px rgba(0,0,0,0.1)'
  },
  tableHead: {
    backgroundColor: '#003366',
    color: 'white'
  },
  approveBtn: {
    backgroundColor: '#007bff',
    color: '#fff',
    padding: '6px 12px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    marginRight: '5px'
  },
  rejectBtn: {
    backgroundColor: '#dc3545',
    color: '#fff',
    padding: '6px 12px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer'
  }
};

export default FulfillRequestsPage;
