import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RequestItemsPage = () => {
  const [inventory, setInventory] = useState([]);
  const [selectedItem, setSelectedItem] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [requests, setRequests] = useState([]);
  const [message, setMessage] = useState('');

  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchInventory = async () => {
      try {
        const res = await axios.get('http://localhost:5050/api/inventory');
        setInventory(res.data);
      } catch (err) {
        console.error('Error fetching inventory:', err);
      }
    };
    fetchInventory();
  }, []);

  const addRequest = () => {
    if (!selectedItem || quantity < 1) return;
    const item = inventory.find(i => i._id === selectedItem);
    setRequests(prev => [...prev, { itemId: selectedItem, name: item.name, quantity }]);
    setQuantity(1);
    setSelectedItem('');
  };

  const submitRequests = async () => {
    try {
      await axios.post('http://localhost:5050/api/requests', {
        requests: requests.map(({ itemId, quantity }) => ({ itemId, quantity })),
        requestedBy: user.username,
      });
      setMessage('✅ Request submitted successfully!');
      setRequests([]);
    } catch (err) {
      console.error('Error submitting request:', err);
      setMessage('❌ Failed to submit request.');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 to-blue-700 text-white py-12 px-6">
      <div className="max-w-4xl mx-auto space-y-8">
        <h1 className="text-4xl font-bold text-white flex items-center gap-2">
          📚 Request Inventory Items
        </h1>

        {/* Item Selection Card */}
        <div className="bg-white text-black rounded-2xl shadow-md p-6 space-y-4">
          <div className="flex flex-wrap items-center gap-4">
            <select
              value={selectedItem}
              onChange={e => setSelectedItem(e.target.value)}
              className="p-2 border rounded-md w-64"
            >
              <option value="">Select Item</option>
              {inventory.map(item => (
                <option key={item._id} value={item._id}>
                  {item.name} (Available: {item.count})
                </option>
              ))}
            </select>

            <input
              type="number"
              value={quantity}
              min="1"
              onChange={e => setQuantity(Number(e.target.value))}
              className="p-2 border rounded-md w-24"
            />

            <button
              onClick={addRequest}
              className="bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700"
            >
              Add
            </button>
          </div>
        </div>

        {/* Request List Card */}
        <div className="bg-white text-black rounded-2xl shadow-md p-6">
          {requests.length > 0 ? (
            <>
              <h2 className="text-lg font-semibold mb-2">📝 Items to Request</h2>
              <ul className="list-disc ml-5 space-y-1">
                {requests.map((r, idx) => (
                  <li key={idx}>
                    {r.name} — Quantity: {r.quantity}
                  </li>
                ))}
              </ul>
            </>
          ) : (
            <p className="text-gray-600">No items added.</p>
          )}
        </div>

        {/* Submit Button */}
        <button
          onClick={submitRequests}
          disabled={requests.length === 0}
          className={`w-full md:w-auto px-6 py-2 rounded-md font-semibold ${
            requests.length === 0
              ? 'bg-gray-400 cursor-not-allowed'
              : 'bg-blue-600 hover:bg-blue-700'
          }`}
        >
          Submit Request
        </button>

        {/* Message */}
        {message && (
          <p className="mt-4 text-yellow-300 font-medium">{message}</p>
        )}
      </div>
    </div>
  );
};

export default RequestItemsPage;
