import React, { useEffect, useState } from 'react';
import axios from 'axios';

const InventoryPage = () => {
  const [inventory, setInventory] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    location: '',
    quantity: ''
  });
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/inventory/all');
      setInventory(res.data);
    } catch (err) {
      console.error('❌ Error fetching inventory:', err);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleAddItem = async () => {
    try {
      await axios.post('http://localhost:5050/api/inventory/add', formData);
      setFormData({ name: '', description: '', location: '', quantity: '' });
      fetchInventory();
    } catch (err) {
      console.error('❌ Error adding item:', err);
    }
  };

  const handleEditClick = (item) => {
    setEditingId(item._id);
    setFormData({
      name: item.name,
      description: item.description,
      location: item.location,
      quantity: item.quantity
    });
  };

  const handleUpdateItem = async () => {
    try {
      await axios.put(`http://localhost:5050/api/inventory/${editingId}`, formData);
      setEditingId(null);
      setFormData({ name: '', description: '', location: '', quantity: '' });
      fetchInventory();
    } catch (err) {
      console.error('❌ Error updating item:', err);
    }
  };

  const handleDeleteItem = async (id) => {
    if (window.confirm('Are you sure you want to delete this item?')) {
      try {
        await axios.delete(`http://localhost:5050/api/inventory/${id}`);
        fetchInventory();
      } catch (err) {
        console.error('❌ Error deleting item:', err);
      }
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Inventory Items</h2>

      <div style={styles.form}>
        <input
          type="text"
          name="name"
          placeholder="Item Name"
          value={formData.name}
          onChange={handleInputChange}
          style={styles.input}
        />
        <input
          type="text"
          name="description"
          placeholder="Description"
          value={formData.description}
          onChange={handleInputChange}
          style={styles.input}
        />
        <input
          type="text"
          name="location"
          placeholder="Location (e.g. A1)"
          value={formData.location}
          onChange={handleInputChange}
          style={styles.input}
        />
        <input
          type="number"
          name="quantity"
          placeholder="Quantity"
          value={formData.quantity}
          onChange={handleInputChange}
          style={styles.input}
        />
        {editingId ? (
          <button onClick={handleUpdateItem} style={styles.updateBtn}>Update</button>
        ) : (
          <button onClick={handleAddItem} style={styles.addBtn}>Add</button>
        )}
      </div>

      <table style={styles.table}>
        <thead>
          <tr>
            <th>Item Name</th>
            <th>Description</th>
            <th>Location</th>
            <th>Count</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {inventory.map(item => (
            <tr key={item._id}>
              <td>{item.name}</td>
              <td>{item.description || '—'}</td>
              <td>{item.location}</td>
              <td>{item.quantity}</td>
              <td>
                <button style={styles.editBtn} onClick={() => handleEditClick(item)}>Edit</button>
                <button style={styles.deleteBtn} onClick={() => handleDeleteItem(item._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// Styling
const styles = {
  container: {
    padding: '30px',
    background: 'linear-gradient(to right, #fdfbfb, #ebedee)',
    minHeight: '100vh',
    fontFamily: 'Arial, sans-serif'
  },
  heading: {
    fontSize: '24px',
    marginBottom: '20px'
  },
  form: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(180px, 1fr))',
    gap: '10px',
    marginBottom: '30px'
  },
  input: {
    padding: '10px',
    borderRadius: '5px',
    border: '1px solid #ccc'
  },
  addBtn: {
    backgroundColor: 'green',
    color: '#fff',
    border: 'none',
    padding: '10px',
    borderRadius: '5px',
    cursor: 'pointer'
  },
  updateBtn: {
    backgroundColor: 'orange',
    color: '#fff',
    border: 'none',
    padding: '10px',
    borderRadius: '5px',
    cursor: 'pointer'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    backgroundColor: '#fff',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)'
  },
  editBtn: {
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    padding: '5px 10px',
    borderRadius: '4px',
    marginRight: '5px',
    cursor: 'pointer'
  },
  deleteBtn: {
    backgroundColor: '#dc3545',
    color: '#fff',
    border: 'none',
    padding: '5px 10px',
    borderRadius: '4px',
    cursor: 'pointer'
  }
};

export default InventoryPage;
