// src/pages/RequestPage.js
import React, { useState } from 'react';
import axios from 'axios';

const RequestPage = () => {
  const [itemName, setItemName] = useState('');
  const [quantity, setQuantity] = useState('');
  const [note, setNote] = useState('');
  const [message, setMessage] = useState('');

  const user = JSON.parse(localStorage.getItem('user'));

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5050/api/requests/add', {
        itemName,
        quantity,
        note,
        requestedBy: user?.username || 'Unknown'
      });

      setMessage(res.data.message);
      setItemName('');
      setQuantity('');
      setNote('');
    } catch (err) {
      console.error(err);
      setMessage('Error submitting request. Try again.');
    }
  };

  return (
    <div style={styles.container}>
      <h2>Submit a Request</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        <input
          type="text"
          placeholder="Item Name"
          value={itemName}
          onChange={(e) => setItemName(e.target.value)}
          required
          style={styles.input}
        />
        <input
          type="number"
          placeholder="Quantity"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          required
          style={styles.input}
        />
        <textarea
          placeholder="Additional Notes"
          value={note}
          onChange={(e) => setNote(e.target.value)}
          rows={4}
          style={styles.textarea}
        />
        <button type="submit" style={styles.button}>Submit Request</button>
        {message && <p style={styles.message}>{message}</p>}
      </form>
    </div>
  );
};

const styles = {
  container: {
    padding: '40px',
    fontFamily: 'Arial, sans-serif'
  },
  form: {
    maxWidth: '400px',
    marginTop: '20px'
  },
  input: {
    width: '100%',
    padding: '10px',
    margin: '10px 0',
    borderRadius: '5px',
    border: '1px solid #ccc'
  },
  textarea: {
    width: '100%',
    padding: '10px',
    margin: '10px 0',
    borderRadius: '5px',
    border: '1px solid #ccc'
  },
  button: {
    padding: '10px 20px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer'
  },
  message: {
    marginTop: '10px',
    color: 'green'
  }
};

export default RequestPage;
