import React from 'react';
import { useNavigate } from 'react-router-dom';

const StembassadorDashboard = () => {
  const user = JSON.parse(localStorage.getItem('user'));
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/');
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h2 style={styles.title}>Welcome, {user?.username || 'STEMbassador'}!</h2>
        <p style={styles.subtitle}>Role: <strong>{user?.role}</strong></p>
        <button onClick={handleLogout} style={styles.logoutButton}>Logout</button>
      </div>

      <div style={styles.grid}>
        <div style={styles.card}>
          <h3>üì¶ View Inventory</h3>
          <p>Browse and manage items in stock.</p>
          <button onClick={() => navigate('/inventory')} style={styles.button}>Go</button>
        </div>

        <div style={styles.card}>
          <h3>üìù Request Items</h3>
          <p>Request items for events or programs.</p>
          <button onClick={() => navigate('/stembassador/request')} style={styles.button}>Go</button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '40px',
    minHeight: '100vh',
    background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)', // Deep blue gradient
    fontFamily: 'Segoe UI, sans-serif',
    color: 'white',
  },
  header: {
    textAlign: 'center',
    marginBottom: '40px',
  },
  title: {
    fontSize: '28px',
    fontWeight: 'bold',
  },
  subtitle: {
    fontSize: '16px',
  },
  logoutButton: {
    marginTop: '12px',
    padding: '10px 16px',
    backgroundColor: '#ff4d4d',
    color: '#fff',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    fontWeight: 'bold',
    transition: 'background-color 0.3s ease',
  },
  grid: {
    display: 'flex',
    justifyContent: 'center',
    gap: '30px',
    flexWrap: 'wrap',
  },
  card: {
    width: '280px',
    backgroundColor: '#ffffff',
    color: '#000',
    padding: '25px',
    borderRadius: '16px',
    boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
    textAlign: 'center',
  },
  button: {
    marginTop: '12px',
    padding: '10px 16px',
    backgroundColor: '#0f2027', // matching dark tone
    color: '#fff',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    fontWeight: 'bold',
    transition: 'background-color 0.3s ease',
  },
};

export default StembassadorDashboard;
