import React from 'react';
import { useNavigate } from 'react-router-dom';

const DirectorDashboard = () => {
  const user = JSON.parse(localStorage.getItem('user'));
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/');
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h2> Welcome, <span style={styles.username}>{user?.username}</span></h2>
        <p> Role: <strong>{user?.role}</strong></p>
        <button onClick={handleLogout} style={styles.logoutBtn}>Logout</button>
      </div>

      <div style={styles.sections}>
        <div style={styles.card}>
          <h3>ðŸ“¦ View Inventory Summary</h3>
          <p>Check stock status across all categories.</p>
          <button style={styles.cardBtn} onClick={() => navigate('/director/inventory')}>Go</button>
        </div>

        <div style={styles.card}>
          <h3>âœ… Review Requests</h3>
          <p>Approve or reject item requests from STEMbassadors.</p>
          <button style={styles.cardBtn} onClick={() => navigate('/approve')}>Go</button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '40px',
    background: 'linear-gradient(to right, #e0f7fa, #f0f9ff)',
    minHeight: '100vh',
    fontFamily: 'Segoe UI, sans-serif',
    color: '#333'
  },
  header: {
    marginBottom: '40px',
    backgroundColor: '#ffffff',
    padding: '25px',
    borderRadius: '12px',
    boxShadow: '0 4px 12px rgba(0,0,0,0.08)',
    textAlign: 'center'
  },
  username: {
    color: '#007bff'
  },
  logoutBtn: {
    marginTop: '15px',
    padding: '10px 20px',
    border: 'none',
    borderRadius: '8px',
    backgroundColor: '#dc3545',
    color: '#fff',
    cursor: 'pointer',
    fontWeight: 'bold',
    transition: '0.3s ease'
  },
  sections: {
    display: 'flex',
    gap: '30px',
    flexWrap: 'wrap',
    justifyContent: 'center'
  },
  card: {
    flex: '1 1 300px',
    backgroundColor: '#ffffff',
    borderRadius: '15px',
    padding: '30px 20px',
    boxShadow: '0 8px 24px rgba(0,0,0,0.1)',
    textAlign: 'center'
  },
  cardBtn: {
    marginTop: '15px',
    padding: '10px 16px',
    border: 'none',
    borderRadius: '6px',
    backgroundColor: '#007bff',
    color: '#fff',
    fontWeight: 'bold',
    cursor: 'pointer',
    fontSize: '14px',
    transition: '0.3s ease'
  }
};

export default DirectorDashboard;
