import React, { useEffect, useState } from 'react';
import axios from 'axios';

function InventoryPage() {
  const [inventory, setInventory] = useState([]);
  const [viewMode, setViewMode] = useState(true);
  const [updateMode, setUpdateMode] = useState(false);
  const [addMode, setAddMode] = useState(false);

  const [newItem, setNewItem] = useState({
    name: '',
    description: '',
    location: '',
    count: ''
  });

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    try {
      const response = await axios.get('http://localhost:5050/api/inventory');
      setInventory(response.data);
    } catch (error) {
      console.error('Error fetching inventory:', error);
    }
  };

  const handleAddItem = async () => {
    try {
      await axios.post('http://localhost:5050/api/inventory/add', newItem);
      fetchInventory();
      setAddMode(false);
      setNewItem({ name: '', description: '', location: '', count: '' });
    } catch (error) {
      console.error('Error adding item:', error);
    }
  };

  const handleDeleteItem = async (id) => {
    try {
      await axios.delete(`http://localhost:5050/api/inventory/${id}`);
      fetchInventory();
    } catch (error) {
      console.error('Error deleting item:', error);
    }
  };

  const handleChange = (e) => {
    setNewItem({ ...newItem, [e.target.name]: e.target.value });
  };

  const groupedInventory = inventory.reduce((acc, item) => {
    const rack = item.location || 'Unknown Rack';
    if (!acc[rack]) acc[rack] = [];
    acc[rack].push(item);
    return acc;
  }, {});

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>üì¶ Inventory Items</h1>
      <div style={styles.buttonGroup}>
        <button onClick={() => { setViewMode(true); setUpdateMode(false); setAddMode(false); }}>View Mode</button>
        <button onClick={() => { setViewMode(false); setUpdateMode(true); setAddMode(false); }}>Update Mode</button>
      </div>

      {updateMode && !addMode && (
        <button onClick={() => setAddMode(true)} style={styles.addButton}>+ Add New Item</button>
      )}

      {addMode && (
        <div style={styles.formContainer}>
          <h3>Add New Item</h3>
          <input name="name" value={newItem.name} onChange={handleChange} placeholder="Item Name" style={styles.input} />
          <input name="description" value={newItem.description} onChange={handleChange} placeholder="Description" style={styles.input} />
          <input name="location" value={newItem.location} onChange={handleChange} placeholder="Location" style={styles.input} />
          <input name="count" value={newItem.count} onChange={handleChange} placeholder="Count" style={styles.input} />
          <div>
            <button onClick={handleAddItem} style={styles.saveButton}>Add</button>
            <button onClick={() => setAddMode(false)} style={styles.cancelButton}>Cancel</button>
          </div>
        </div>
      )}

      {viewMode && Object.entries(groupedInventory).map(([rack, items]) => (
        <div key={rack} style={styles.rackContainer}>
          <div style={styles.rackTitle}>üìç Rack {rack}</div>
          <table style={styles.table}>
            <thead>
              <tr>
                <th style={styles.th}>Item Name</th>
                <th style={styles.th}>Description</th>
                <th style={styles.th}>Count</th>
              </tr>
            </thead>
            <tbody>
              {items.map(item => (
                <tr key={item._id}>
                  <td style={styles.td}>{item.name}</td>
                  <td style={styles.td}>{item.description || '‚Äî'}</td>
                  <td style={styles.td}>{item.count || '‚Äî'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}

      {updateMode && !addMode && (
        <table style={styles.table}>
          <thead>
            <tr>
              <th style={styles.th}>Item Name</th>
              <th style={styles.th}>Description</th>
              <th style={styles.th}>Location</th>
              <th style={styles.th}>Count</th>
              <th style={styles.th}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {inventory.map(item => (
              <tr key={item._id}>
                <td style={styles.td}>{item.name}</td>
                <td style={styles.td}>{item.description || '‚Äî'}</td>
                <td style={styles.td}>{item.location || '‚Äî'}</td>
                <td style={styles.td}>{item.count}</td>
                <td style={styles.td}>
                  <button style={styles.editButton}>Edit</button>
                  <button style={styles.deleteButton} onClick={() => handleDeleteItem(item._id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

const styles = {
  container: {
    padding: '2rem',
    fontFamily: 'Arial, sans-serif',
    background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
    color: '#fff',
    minHeight: '100vh'
  },
  heading: {
    fontSize: '1.8rem',
    marginBottom: '1rem'
  },
  buttonGroup: {
    display: 'flex',
    gap: '10px',
    marginBottom: '1rem'
  },
  addButton: {
    backgroundColor: 'green',
    color: 'white',
    padding: '10px',
    marginBottom: '1rem',
    border: 'none',
    borderRadius: '5px'
  },
  formContainer: {
    backgroundColor: 'white',
    color: 'black',
    padding: '1rem',
    borderRadius: '10px',
    width: '350px',
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
    marginBottom: '2rem'
  },
  input: {
    padding: '10px',
    borderRadius: '5px',
    border: '1px solid #ccc'
  },
  saveButton: {
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    padding: '5px 10px',
    marginRight: '10px'
  },
  cancelButton: {
    backgroundColor: '#dc3545',
    color: '#fff',
    border: 'none',
    padding: '5px 10px'
  },
  table: {
    width: '100%',
    backgroundColor: 'white',
    color: 'black',
    marginBottom: '2rem',
    borderCollapse: 'collapse'
  },
  th: {
    borderBottom: '2px solid #ccc',
    padding: '10px',
    textAlign: 'left',
    backgroundColor: '#f2f2f2'
  },
  td: {
    borderBottom: '1px solid #ccc',
    padding: '10px'
  },
  editButton: {
    backgroundColor: '#007bff',
    color: '#fff',
    padding: '5px 10px',
    border: 'none',
    marginRight: '5px'
  },
  deleteButton: {
    backgroundColor: '#dc3545',
    color: '#fff',
    padding: '5px 10px',
    border: 'none'
  },
  rackContainer: {
    backgroundColor: 'white',
    padding: '1rem',
    color: 'black',
    marginBottom: '2rem',
    borderRadius: '10px'
  },
  rackTitle: {
    fontWeight: 'bold',
    fontSize: '1.2rem',
    marginBottom: '1rem'
  }
};

export default InventoryPage;
