import React, { useEffect, useState } from 'react';
import axios from 'axios';

const FulfillRequestsPage = () => {
  const [requests, setRequests] = useState([]);
  const [message, setMessage] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchRequests();
  }, []);

  const fetchRequests = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/requests/all'); // Ensure this returns all with status
      setRequests(res.data);
    } catch (err) {
      console.error('Error fetching requests:', err);
    }
  };

  const handleAction = async (id, action) => {
    try {
      await axios.put(`http://localhost:5050/api/requests/${action}/${id}`);
      setMessage(`Request ${action}ed successfully.`);
      fetchRequests();
    } catch (err) {
      console.error(`Error trying to ${action} request:`, err);
      setMessage(`Failed to ${action} request.`);
    }
  };

  const exportCSV = () => {
    const fulfilled = requests.filter(req => req.status === 'approved' || req.status === 'rejected');
    const csvData = fulfilled.map((req) => ({
      Item: req.itemName || req.itemId?.name || 'Unknown',
      Quantity: req.quantity,
      RequestedBy: req.requestedBy,
      Status: req.status,
    }));

    const headers = Object.keys(csvData[0]).join(',');
    const rows = csvData.map(obj => Object.values(obj).join(','));
    const csvString = [headers, ...rows].join('\n');

    const blob = new Blob([csvString], { type: 'text/csv' });
    const a = document.createElement('a');
    a.href = URL.createObjectURL(blob);
    a.download = 'fulfilled_requests.csv';
    a.click();
  };

  const filteredRequests = requests.filter(
    req =>
      (req.itemName || req.itemId?.name || 'Unknown').toLowerCase().includes(searchTerm.toLowerCase()) ||
      req.requestedBy?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const total = filteredRequests.length;
  const approvedCount = filteredRequests.filter(req => req.status === 'approved').length;
  const rejectedCount = filteredRequests.filter(req => req.status === 'rejected').length;
  const pendingCount = filteredRequests.filter(req => req.status === 'pending').length;

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>üì¨ Fulfill Requests</h2>

      {message && <p style={styles.message}>{message}</p>}

      <div style={styles.controls}>
        <input
          type="text"
          placeholder="Search by item or user..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={styles.search}
        />
        <button onClick={exportCSV} style={styles.exportButton}>Export</button>
      </div>

      <div style={styles.summary}>
        <strong>Summary:</strong> Total: {total} | ‚úÖ Approved: {approvedCount} | ‚ùå Rejected: {rejectedCount} | ‚è≥ Pending: {pendingCount}
      </div>

      {filteredRequests.length === 0 ? (
        <p>No matching requests found.</p>
      ) : (
        <table style={styles.table}>
          <thead>
            <tr>
              <th>Item</th>
              <th>Quantity</th>
              <th>Requested By</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredRequests.map((req) => (
              <tr key={req._id}>
                <td>{req.itemName || req.itemId?.name || 'Unknown'}</td>
                <td>{req.quantity}</td>
                <td>{req.requestedBy}</td>
                <td>
                  <button
                    onClick={() => handleAction(req._id, 'approve')}
                    style={styles.approveBtn}
                  >
                    Approve
                  </button>
                  <button
                    onClick={() => handleAction(req._id, 'reject')}
                    style={styles.rejectBtn}
                  >
                    Reject
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    fontFamily: 'Arial, sans-serif',
    background: 'linear-gradient(to right, #1d2b36, #234050)',
    minHeight: '100vh',
    color: '#fff'
  },
  heading: {
    color: '#ffffff'
  },
  message: {
    padding: '10px',
    marginBottom: '15px',
    backgroundColor: '#d4edda',
    color: '#155724',
    border: '1px solid #c3e6cb',
    borderRadius: '5px',
    fontWeight: 'bold'
  },
  controls: {
    display: 'flex',
    justifyContent: 'space-between',
    margin: '15px 0',
    gap: '10px',
    flexWrap: 'wrap'
  },
  search: {
    padding: '8px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    width: '250px'
  },
  exportButton: {
    backgroundColor: '#007bff',
    color: '#fff',
    padding: '8px 15px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer'
  },
  summary: {
    backgroundColor: '#e3f2fd',
    color: '#000',
    padding: '10px',
    borderRadius: '5px',
    marginBottom: '10px'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    backgroundColor: '#fff',
    color: '#000',
    boxShadow: '0 0 8px rgba(0,0,0,0.1)'
  },
  approveBtn: {
    backgroundColor: '#007bff',
    color: '#fff',
    padding: '6px 12px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    marginRight: '5px'
  },
  rejectBtn: {
    backgroundColor: '#dc3545',
    color: '#fff',
    padding: '6px 12px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer'
  }
};

export default FulfillRequestsPage;
