import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ApprovePage = () => {
  const [requests, setRequests] = useState([]);
  const [updatingId, setUpdatingId] = useState(null);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchRequests();
  }, []);

  const fetchRequests = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/requests');
      setRequests(res.data);
    } catch (err) {
      console.error('Failed to fetch requests:', err);
    }
  };

  const updateStatus = async (id, newStatus) => {
    try {
      setUpdatingId(id);
      await axios.put(`http://localhost:5050/api/requests/${id}`, { status: newStatus });
      setMessage(`Request ${newStatus.toLowerCase()} successfully.`);
      fetchRequests();
    } catch (err) {
      console.error('Failed to update request:', err);
      setMessage('Something went wrong.');
    } finally {
      setUpdatingId(null);
    }
  };

  const exportCSV = () => {
    const headers = ['Item Name', 'Quantity', 'Note', 'Requested By', 'Status'];
    const rows = requests.map(r =>
      [r.itemName, r.quantity, r.note || '', r.requestedBy, r.status]
    );

    let csvContent = 'data:text/csv;charset=utf-8,'
      + headers.join(',') + '\n'
      + rows.map(e => e.join(',')).join('\n');

    const encodedUri = encodeURI(csvContent);
    const link = document.createElement('a');
    link.setAttribute('href', encodedUri);
    link.setAttribute('download', 'requests_export.csv');
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>âœ… Review Item Requests</h2>

      <div style={styles.summary}>
        <p>ðŸ“Š <strong>Request Summary</strong></p>
        <p>
          Total: {requests.length} |{' '}
          Approved: {requests.filter(r => r.status === 'Approved').length} |{' '}
          Pending: {requests.filter(r => r.status === 'Pending').length} |{' '}
          Rejected: {requests.filter(r => r.status === 'Rejected').length}
        </p>
        <button onClick={exportCSV} style={styles.exportBtn}>ðŸ“¥ Export as CSV</button>
      </div>

      {message && <p style={styles.message}>{message}</p>}

      {requests.length === 0 ? (
        <p style={{ color: 'white' }}>No requests found.</p>
      ) : (
        <table style={styles.table}>
          <thead>
            <tr>
              <th>Item</th>
              <th>Quantity</th>
              <th>Note</th>
              <th>Requested By</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {requests.map((req, index) => (
              <tr key={req._id}>
                <td>{req.itemName}</td>
                <td>{req.quantity}</td>
                <td>{req.note || 'â€”'}</td>
                <td>{req.requestedBy}</td>
                <td>
                  <span style={{
                    fontWeight: 'bold',
                    color: req.status === 'Approved' ? 'lightgreen' :
                           req.status === 'Rejected' ? '#ff6b6b' : '#ffe066'
                  }}>
                    {req.status}
                  </span>
                </td>
                <td>
                  {req.status === 'Pending' ? (
                    <>
                      <button
                        style={{ ...styles.btn, backgroundColor: '#28a745' }}
                        onClick={() => updateStatus(req._id, 'Approved')}
                        disabled={updatingId === req._id}
                      >
                        Approve
                      </button>
                      <button
                        style={{ ...styles.btn, backgroundColor: '#dc3545' }}
                        onClick={() => updateStatus(req._id, 'Rejected')}
                        disabled={updatingId === req._id}
                      >
                        Reject
                      </button>
                    </>
                  ) : (
                    <em style={{ color: 'gray' }}>â€”</em>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    fontFamily: 'Arial, sans-serif',
    background: 'linear-gradient(to bottom right, #0f2027, #203a43, #2c5364)',
    minHeight: '100vh',
    color: 'white'
  },
  title: {
    marginBottom: '20px'
  },
  message: {
    backgroundColor: '#e0f7e9',
    padding: '10px',
    borderRadius: '5px',
    color: '#155724',
    fontWeight: 'bold',
    marginBottom: '10px',
    border: '1px solid #c3e6cb'
  },
  summary: {
    backgroundColor: '#ffffff',
    padding: '15px',
    margin: '20px 0',
    borderRadius: '8px',
    boxShadow: '0 0 6px rgba(0,0,0,0.08)',
    color: '#000'
  },
  exportBtn: {
    marginTop: '10px',
    padding: '8px 16px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    fontWeight: 'bold',
    cursor: 'pointer'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    marginTop: '20px',
    backgroundColor: '#ffffff',
    color: '#000',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)',
    borderRadius: '8px',
    overflow: 'hidden'
  },
  btn: {
    color: 'white',
    border: 'none',
    padding: '6px 10px',
    marginRight: '5px',
    borderRadius: '4px',
    cursor: 'pointer'
  },
  recentRow: {
    backgroundColor: '#f3f3f3'
  }
};

export default ApprovePage;
