// frontend/src/pages/ApprovePage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ApprovePage = () => {
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchRequests = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/requests');
      setRequests(res.data);
    } catch (err) {
      console.error('Failed to fetch requests:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateStatus = async (id, newStatus) => {
    try {
      await axios.put(`http://localhost:5050/api/requests/${id}`, { status: newStatus });
      fetchRequests(); // refresh list
    } catch (err) {
      console.error('Failed to update request:', err);
    }
  };

  useEffect(() => {
    fetchRequests();
  }, []);

  return (
    <div style={styles.container}>
      <h2>Approve Requests</h2>
      {loading ? (
        <p>Loading...</p>
      ) : requests.length === 0 ? (
        <p>No requests found.</p>
      ) : (
        <table style={styles.table}>
          <thead>
            <tr>
              <th>Item</th>
              <th>Quantity</th>
              <th>Note</th>
              <th>Requested By</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {requests.map((req) => (
              <tr key={req._id}>
                <td>{req.itemName}</td>
                <td>{req.quantity}</td>
                <td>{req.note}</td>
                <td>{req.requestedBy}</td>
                <td>{req.status}</td>
                <td>
                  {req.status === 'Pending' ? (
                    <>
                      <button onClick={() => handleUpdateStatus(req._id, 'Approved')} style={styles.approve}>Approve</button>
                      <button onClick={() => handleUpdateStatus(req._id, 'Rejected')} style={styles.reject}>Reject</button>
                    </>
                  ) : (
                    <span>{req.status}</span>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '30px'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse'
  },
  approve: {
    backgroundColor: 'green',
    color: 'white',
    border: 'none',
    padding: '6px 10px',
    marginRight: '5px',
    borderRadius: '4px',
    cursor: 'pointer'
  },
  reject: {
    backgroundColor: 'red',
    color: 'white',
    border: 'none',
    padding: '6px 10px',
    borderRadius: '4px',
    cursor: 'pointer'
  }
};

export default ApprovePage;
