import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RequestItemsPage = () => {
  const [inventory, setInventory] = useState([]);
  const [selectedItem, setSelectedItem] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [requests, setRequests] = useState([]);
  const [message, setMessage] = useState('');
  const [requestHistory, setRequestHistory] = useState([]);

  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchInventory = async () => {
      try {
        const res = await axios.get('http://localhost:5050/api/inventory');
        setInventory(res.data);
      } catch (err) {
        console.error('Error fetching inventory:', err);
      }
    };

    const fetchHistory = async () => {
      try {
        const res = await axios.get(`http://localhost:5050/api/requests/${user.username}`);
        setRequestHistory(res.data);
      } catch (err) {
        console.error('Error fetching request history:', err);
      }
    };

    fetchInventory();
    fetchHistory();
  }, [user.username]);

  const addRequest = () => {
    if (!selectedItem || quantity < 1) return;
    const item = inventory.find(i => i._id === selectedItem);
    setRequests(prev => [...prev, { itemId: selectedItem, name: item.name, quantity }]);
    setQuantity(1);
    setSelectedItem('');
  };

  const submitRequests = async () => {
    try {
      await axios.post('http://localhost:5050/api/requests', {
        requests: requests.map(({ itemId, quantity }) => ({ itemId, quantity })),
        requestedBy: user.username,
      });
      setMessage('‚úÖ Request submitted successfully!');
      setRequests([]);
      const historyRes = await axios.get(`http://localhost:5050/api/requests/${user.username}`);
      setRequestHistory(historyRes.data);
    } catch (err) {
      console.error('Error submitting request:', err);
      setMessage('‚ùå Failed to submit request.');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 to-blue-600 text-white py-12 px-4">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-4xl font-bold mb-8 flex items-center gap-2">
          üìö Request Inventory Items
        </h1>

        {/* Add Item Section */}
        <div className="bg-white text-black rounded-lg shadow-lg p-6 mb-6">
          <div className="flex flex-col md:flex-row gap-4">
            <select
              value={selectedItem}
              onChange={e => setSelectedItem(e.target.value)}
              className="p-2 border rounded w-full md:w-1/2"
            >
              <option value="">Select Item</option>
              {inventory.map(item => (
                <option key={item._id} value={item._id}>
                  {item.name} (Available: {item.count})
                </option>
              ))}
            </select>

            <input
              type="number"
              value={quantity}
              min="1"
              onChange={e => setQuantity(Number(e.target.value))}
              className="p-2 border rounded w-full md:w-1/4"
            />

            <button
              onClick={addRequest}
              className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 w-full md:w-auto"
            >
              ‚ûï Add
            </button>
          </div>
        </div>

        {/* Pending Requests */}
        {requests.length > 0 && (
          <div className="bg-white text-black rounded-lg shadow-lg p-6 mb-6">
            <h2 className="text-xl font-semibold mb-3">üìù Items to Request:</h2>
            <ul className="list-disc pl-6 space-y-1">
              {requests.map((r, idx) => (
                <li key={idx}>
                  {r.name} ‚Äî Quantity: {r.quantity}
                </li>
              ))}
            </ul>
          </div>
        )}

        {/* Submit Button */}
        <button
          onClick={submitRequests}
          disabled={requests.length === 0}
          className={`mb-6 px-6 py-2 rounded transition duration-300 ${
            requests.length === 0
              ? 'bg-gray-400 cursor-not-allowed'
              : 'bg-blue-500 hover:bg-blue-700'
          }`}
        >
          Submit Request
        </button>

        {message && <p className="mb-6 text-yellow-200 font-medium">{message}</p>}

        {/* Previous Request History */}
        <div className="mt-10 bg-white text-black rounded-lg shadow-lg p-6">
          <h2 className="text-xl font-semibold mb-4 flex items-center gap-2">
            üìú Your Previous Requests
          </h2>

          {requestHistory.length > 0 ? (
            <div className="overflow-x-auto">
              <table className="min-w-full table-auto border border-gray-300 text-sm">
                <thead>
                  <tr className="bg-blue-100 text-left">
                    <th className="px-4 py-2 border-b">Item Name</th>
                    <th className="px-4 py-2 border-b">Quantity</th>
                    <th className="px-4 py-2 border-b">Status</th>
                    <th className="px-4 py-2 border-b">Requested On</th>
                  </tr>
                </thead>
                <tbody>
                  {requestHistory.map((req, idx) => (
                    <tr key={idx} className="hover:bg-blue-50">
                      <td className="px-4 py-2 border-b">
                        {req.itemId?.name || 'Unknown'}
                      </td>
                      <td className="px-4 py-2 border-b">{req.quantity}</td>
                      <td className="px-4 py-2 border-b capitalize">
                        <span
                          className={`font-medium ${
                            req.status === 'approved'
                              ? 'text-green-600'
                              : req.status === 'rejected'
                              ? 'text-red-600'
                              : 'text-yellow-600'
                          }`}
                        >
                          {req.status}
                        </span>
                      </td>
                      <td className="px-4 py-2 border-b">
                        {new Date(req.createdAt).toLocaleString()}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <p className="text-gray-600">No request history available.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default RequestItemsPage;
