import React, { useEffect, useState } from 'react';
import axios from 'axios';

const InventoryPage = () => {
  const [inventory, setInventory] = useState([]);
  const [viewMode, setViewMode] = useState('update');
  const [isEditing, setIsEditing] = useState(false);
  const [currentItem, setCurrentItem] = useState(null);

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/inventory/');
      console.log("Fetched Inventory:", res.data);
      setInventory(res.data);
    } catch (err) {
      console.error('Error fetching inventory:', err);
    }
  };

  const handleUpdateItem = async () => {
    try {
      await axios.put(`http://localhost:5050/api/inventory/${currentItem._id}`, currentItem);
      setIsEditing(false);
      fetchInventory();
    } catch (err) {
      console.error('Error updating item:', err);
    }
  };

  const groupByRack = (items) => {
    const grouped = {};
    items.forEach(item => {
      const rack = item.location && item.location.trim() !== '' 
        ? item.location.toUpperCase() 
        : 'Unknown Rack';
      if (!grouped[rack]) grouped[rack] = [];
      grouped[rack].push(item);
    });
    console.log("Grouped Inventory:", grouped);
    return grouped;
  };

  return (
    <div style={styles.container}>
      <h2>📦 Inventory Items</h2>
      <div style={styles.toggleBtns}>
        <button onClick={() => setViewMode('view')} style={styles.toggleBtn}>View Mode</button>
        <button onClick={() => setViewMode('update')} style={styles.toggleBtn}>Update Mode</button>
      </div>

      {viewMode === 'view' ? (
        Object.keys(groupByRack(inventory)).length === 0 ? (
          <p>No items available to display.</p>
        ) : (
          Object.entries(groupByRack(inventory)).map(([rack, items]) => (
            <div key={rack} style={styles.rackGroup}>
              <h3>📍 Rack {rack}</h3>
              <table style={styles.table}>
                <thead>
                  <tr>
                    <th>Item Name</th>
                    <th>Description</th>
                    <th>Count</th>
                  </tr>
                </thead>
                <tbody>
                  {items.map(item => (
                    <tr key={item._id}>
                      <td>{item.name}</td>
                      <td>{item.description || '—'}</td>
                      <td>{item.count}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))
        )
      ) : (
        <table style={styles.table}>
          <thead>
            <tr>
              <th>Item Name</th>
              <th>Description</th>
              <th>Location</th>
              <th>Count</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {inventory.map(item => (
              <tr key={item._id}>
                <td>{item.name}</td>
                <td>{item.description || '—'}</td>
                <td>{item.location}</td>
                <td>{item.count}</td>
                <td>
                  <button style={styles.editBtn} onClick={() => {
                    setCurrentItem(item);
                    setIsEditing(true);
                  }}>Edit</button>
                  <button style={styles.deleteBtn}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {isEditing && (
        <div style={styles.modalBackdrop}>
          <div style={styles.modal}>
            <h3>Edit Item</h3>
            <label>Name:</label>
            <input value={currentItem.name} onChange={(e) => setCurrentItem({ ...currentItem, name: e.target.value })} />
            <label>Description:</label>
            <input value={currentItem.description} onChange={(e) => setCurrentItem({ ...currentItem, description: e.target.value })} />
            <label>Location:</label>
            <input value={currentItem.location} onChange={(e) => setCurrentItem({ ...currentItem, location: e.target.value })} />
            <label>Count:</label>
            <input type="number" value={currentItem.count} onChange={(e) => setCurrentItem({ ...currentItem, count: e.target.value })} />
            <div style={{ marginTop: '10px' }}>
              <button onClick={handleUpdateItem} style={styles.saveBtn}>Save</button>
              <button onClick={() => setIsEditing(false)} style={styles.cancelBtn}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
    minHeight: '100vh',
    color: 'white'
  },
  toggleBtns: {
    marginBottom: '20px',
    display: 'flex',
    gap: '10px'
  },
  toggleBtn: {
    backgroundColor: '#1e90ff',
    border: 'none',
    color: 'white',
    padding: '8px 16px',
    borderRadius: '6px',
    cursor: 'pointer',
    fontWeight: 'bold'
  },
  rackGroup: {
    backgroundColor: 'white',
    color: 'black',
    padding: '10px',
    borderRadius: '8px',
    marginBottom: '20px'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    marginTop: '10px',
    backgroundColor: '#fff',
    color: '#000'
  },
  editBtn: {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '6px 10px',
    marginRight: '5px',
    border: 'none',
    borderRadius: '4px'
  },
  deleteBtn: {
    backgroundColor: '#dc3545',
    color: 'white',
    padding: '6px 10px',
    border: 'none',
    borderRadius: '4px'
  },
  modalBackdrop: {
    position: 'fixed',
    top: 0, left: 0, right: 0, bottom: 0,
    backgroundColor: 'rgba(0,0,0,0.5)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 1000
  },
  modal: {
    backgroundColor: '#fff',
    padding: '20px',
    borderRadius: '8px',
    color: '#000',
    display: 'flex',
    flexDirection: 'column',
    width: '300px',
    gap: '10px'
  },
  saveBtn: {
    backgroundColor: '#28a745',
    color: 'white',
    padding: '6px 10px',
    border: 'none',
    borderRadius: '4px',
    marginRight: '8px'
  },
  cancelBtn: {
    backgroundColor: '#6c757d',
    color: 'white',
    padding: '6px 10px',
    border: 'none',
    borderRadius: '4px'
  }
};

export default InventoryPage;
