import React, { useEffect, useState } from 'react';
import axios from 'axios';

const InventoryPage = () => {
  const [items, setItems] = useState([]);

  useEffect(() => {
    // Fetch all inventory items
    const fetchItems = async () => {
      try {
        const res = await axios.get('http://localhost:5050/api/inventory/all');
        setItems(res.data);
      } catch (err) {
        console.error('Error fetching inventory:', err);
      }
    };

    fetchItems();
  }, []);

  return (
    <div style={styles.container}>
      <h2 style={styles.header}>ðŸ“¦ Inventory Items</h2>

      {items.length === 0 ? (
        <p style={styles.emptyText}>No items found.</p>
      ) : (
        <div style={styles.grid}>
          {items.map((item) => (
            <div key={item._id} style={styles.card}>
              <h3>{item.name}</h3>
              <p><strong>Quantity:</strong> {item.quantity}</p>
              <p><strong>Category:</strong> {item.category}</p>
              <p><strong>Description:</strong> {item.description}</p>
              <p style={styles.timestamp}><em>Added: {new Date(item.createdAt).toLocaleString()}</em></p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    background: '#f2fef2',
    minHeight: '100vh',
    fontFamily: 'Arial, sans-serif'
  },
  header: {
    fontSize: '24px',
    marginBottom: '20px'
  },
  emptyText: {
    fontSize: '16px',
    color: '#777'
  },
  grid: {
    display: 'flex',
    flexWrap: 'wrap',
    gap: '20px'
  },
  card: {
    backgroundColor: '#fff',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0px 2px 10px rgba(0,0,0,0.1)',
    width: '280px'
  },
  timestamp: {
    marginTop: '10px',
    fontSize: '12px',
    color: '#666'
  }
};

export default InventoryPage;
