import React, { useState } from 'react';
import axios from 'axios';

const AddUserPage = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    confirmPassword: '',
    role: 'inventoryManager'
  });
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match.');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const res = await axios.post('http://localhost:5050/api/auth/signup', formData, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      setMessage('User added successfully.');
      setError('');
      setFormData({ username: '', password: '', confirmPassword: '', role: 'inventoryManager' });
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.message || 'Error creating user.');
      setMessage('');
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.box}>
        <h2>Add New User</h2>
        <form onSubmit={handleSubmit} style={styles.form}>
          <input
            type="text"
            name="username"
            placeholder="Username"
            value={formData.username}
            onChange={handleChange}
            style={styles.input}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            style={styles.input}
            required
          />
          <input
            type="password"
            name="confirmPassword"
            placeholder="Confirm Password"
            value={formData.confirmPassword}
            onChange={handleChange}
            style={styles.input}
            required
          />
          <select name="role" value={formData.role} onChange={handleChange} style={styles.input}>
            <option value="director">Director</option>
            <option value="inventoryManager">Inventory Manager</option>
            <option value="stembassador">STEMbassador</option>
          </select>
          <button type="submit" style={styles.button}>Add User</button>
        </form>

        {message && <p style={styles.success}>{message}</p>}
        {error && <p style={styles.error}>{error}</p>}
      </div>
    </div>
  );
};

const styles = {
  container: {
    height: '100vh',
    background: 'linear-gradient(to right, #fbc2eb, #a6c1ee)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },
  box: {
    backgroundColor: '#fff',
    padding: '30px',
    borderRadius: '12px',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)',
    width: '350px',
    textAlign: 'center'
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '10px'
  },
  input: {
    padding: '10px',
    borderRadius: '6px',
    border: '1px solid #ccc',
    fontSize: '14px'
  },
  button: {
    backgroundColor: '#007bff',
    color: '#fff',
    padding: '10px',
    border: 'none',
    borderRadius: '6px',
    cursor: 'pointer',
    fontWeight: 'bold'
  },
  success: {
    color: 'green',
    marginTop: '10px'
  },
  error: {
    color: 'red',
    marginTop: '10px'
  }
};

export default AddUserPage;
