import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RequestItemsPage = () => {
  const [inventory, setInventory] = useState([]);
  const [selectedItem, setSelectedItem] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [requests, setRequests] = useState([]);
  const [message, setMessage] = useState('');
  const [requestHistory, setRequestHistory] = useState([]);

  const user = JSON.parse(localStorage.getItem('user'));

  // Fetch inventory
  useEffect(() => {
    const fetchInventory = async () => {
      try {
        const res = await axios.get('http://localhost:5050/api/inventory');
        setInventory(res.data);
      } catch (err) {
        console.error('Error fetching inventory:', err);
      }
    };
    fetchInventory();
  }, []);

  // Fetch past requests
  const fetchRequestHistory = async () => {
    try {
      const res = await axios.get(`http://localhost:5050/api/requests/user/${user.username}`);
      setRequestHistory(res.data);
    } catch (err) {
      console.error('Error fetching request history:', err);
    }
  };

  useEffect(() => {
    fetchRequestHistory();
  }, []);

  const addRequest = () => {
    if (!selectedItem || quantity < 1) return;
    const item = inventory.find(i => i._id === selectedItem);
    setRequests(prev => [...prev, { itemId: selectedItem, name: item.name, quantity }]);
    setQuantity(1);
    setSelectedItem('');
  };

  const submitRequests = async () => {
    try {
      await axios.post('http://localhost:5050/api/requests', {
        requests: requests.map(({ itemId, quantity }) => ({ itemId, quantity })),
        requestedBy: user.username,
      });
      setMessage('✅ Request submitted successfully!');
      setRequests([]);
      fetchRequestHistory(); // Refresh history
    } catch (err) {
      console.error('Error submitting request:', err);
      setMessage('❌ Failed to submit request.');
    }
  };

  // Grouped latest requests per item
  const groupedRequests = Object.values(
    requestHistory.reduce((acc, req) => {
      const key = req.itemId?.name || 'Unknown';
      if (!acc[key] || new Date(req.requestedAt) > new Date(acc[key].requestedAt)) {
        acc[key] = req;
      }
      return acc;
    }, {})
  );

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-900 to-blue-700 text-white py-12 px-4">
      <div className="max-w-3xl mx-auto">
        <h1 className="text-4xl font-bold mb-8 flex items-center gap-2">
          📚 Request Inventory Items
        </h1>

        {/* Request Form */}
        <div className="bg-white text-black rounded-lg shadow-lg p-6 mb-6">
          <div className="flex flex-col md:flex-row gap-4">
            <select
              value={selectedItem}
              onChange={e => setSelectedItem(e.target.value)}
              className="p-2 border rounded w-full md:w-1/2"
            >
              <option value="">Select Item</option>
              {inventory.map(item => (
                <option key={item._id} value={item._id}>
                  {item.name} (Available: {item.count})
                </option>
              ))}
            </select>

            <input
              type="number"
              value={quantity}
              min="1"
              onChange={e => setQuantity(Number(e.target.value))}
              className="p-2 border rounded w-full md:w-1/4"
            />

            <button
              onClick={addRequest}
              className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 w-full md:w-auto"
            >
              ➕ Add
            </button>
          </div>
        </div>

        {/* Items to Request */}
        {requests.length > 0 && (
          <div className="bg-white text-black rounded-lg shadow-lg p-6 mb-6">
            <h2 className="text-xl font-semibold mb-3">📝 Items to Request:</h2>
            <ul className="list-disc pl-6 space-y-1">
              {requests.map((r, idx) => (
                <li key={idx}>
                  {r.name} — Quantity: {r.quantity}
                </li>
              ))}
            </ul>
          </div>
        )}

        {/* Submit Button */}
        <button
          onClick={submitRequests}
          disabled={requests.length === 0}
          className={`px-6 py-2 rounded transition duration-300 ${
            requests.length === 0
              ? 'bg-gray-400 cursor-not-allowed'
              : 'bg-blue-600 hover:bg-blue-700'
          }`}
        >
          Submit Request
        </button>

        {message && <p className="mt-4 text-yellow-200 font-medium">{message}</p>}

        {/* Past Request History */}
        <div className="mt-10">
          <h2 className="text-2xl font-semibold mb-4">📜 Past Requests</h2>
          {groupedRequests.length === 0 ? (
            <p className="text-white">No past requests found.</p>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {groupedRequests.map((req, index) => (
                <div
                  key={index}
                  className="bg-white text-black p-4 rounded-lg shadow-md hover:shadow-xl transition duration-300"
                >
                  <p><strong>🧾 Item:</strong> {req.itemId?.name || 'Unknown'}</p>
                  <p><strong>🔢 Quantity:</strong> {req.quantity}</p>
                  <p><strong>⏳ Status:</strong> <span className={`font-semibold ${req.status === 'pending' ? 'text-yellow-600' : req.status === 'approved' ? 'text-green-600' : 'text-red-600'}`}>{req.status}</span></p>
                  <p><strong>📅 Date:</strong> {new Date(req.requestedAt).toLocaleString()}</p>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default RequestItemsPage;
