import React, { useEffect, useState } from 'react';
import axios from 'axios';

function RequestItemsPage() {
  const [inventory, setInventory] = useState([]);
  const [requests, setRequests] = useState({});
  const [message, setMessage] = useState('');

  useEffect(() => {
    axios.get('/api/inventory')  // adjust path if different
      .then(res => setInventory(res.data))
      .catch(err => console.error('Error fetching inventory:', err));
  }, []);

  const handleChange = (itemId, quantity) => {
    setRequests(prev => ({
      ...prev,
      [itemId]: quantity
    }));
  };

  const handleSubmit = () => {
    const payload = Object.entries(requests)
      .filter(([_, qty]) => qty > 0)
      .map(([itemId, qty]) => ({
        itemId,
        quantity: Number(qty),
        status: 'pending'
      }));

    if (payload.length === 0) {
      setMessage('Please select at least one item to request.');
      return;
    }

    axios.post('/api/requests', { requests: payload, requestedBy: 'stembassador' }) // or include user ID
      .then(() => {
        setMessage('Request submitted successfully!');
        setRequests({});
      })
      .catch(err => {
        console.error(err);
        setMessage('Error submitting request.');
      });
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">ðŸ“¬ Request Inventory Items</h2>

      {inventory.length === 0 ? (
        <p>No items available.</p>
      ) : (
        <table className="table-auto w-full mb-4 border">
          <thead>
            <tr className="bg-gray-100">
              <th className="px-4 py-2">Item</th>
              <th className="px-4 py-2">Location</th>
              <th className="px-4 py-2">Available</th>
              <th className="px-4 py-2">Request Qty</th>
            </tr>
          </thead>
          <tbody>
            {inventory.map(item => (
              <tr key={item._id}>
                <td className="border px-4 py-2">{item.itemName}</td>
                <td className="border px-4 py-2">{item.location}</td>
                <td className="border px-4 py-2">{item.count}</td>
                <td className="border px-4 py-2">
                  <input
                    type="number"
                    min="0"
                    value={requests[item._id] || ''}
                    onChange={(e) => handleChange(item._id, e.target.value)}
                    className="border px-2 py-1 w-20"
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <button
        onClick={handleSubmit}
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        Submit Request
      </button>

      {message && <p className="mt-4 text-sm">{message}</p>}
    </div>
  );
}

export default RequestItemsPage;
