import React, { useEffect, useState } from 'react';
import axios from 'axios';

const InventoryPage = () => {
  const [inventory, setInventory] = useState([]);
  const [editingItem, setEditingItem] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    location: '',
    quantity: ''
  });

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/inventory/all');
      setInventory(res.data);
    } catch (err) {
      console.error('Error fetching inventory:', err);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this item?')) {
      try {
        await axios.delete(`http://localhost:5050/api/inventory/${id}`);
        fetchInventory();
      } catch (err) {
        console.error('Delete error:', err);
      }
    }
  };

  const handleEditClick = (item) => {
    setEditingItem(item._id);
    setFormData({
      name: item.name,
      description: item.description,
      location: item.location,
      quantity: item.quantity
    });
  };

  const handleUpdate = async () => {
    try {
      await axios.put(`http://localhost:5050/api/inventory/${editingItem}`, formData);
      setEditingItem(null);
      fetchInventory();
    } catch (err) {
      console.error('Update error:', err);
    }
  };

  const handleAddItem = async () => {
    try {
      await axios.post('http://localhost:5050/api/inventory/add', formData);
      setFormData({ name: '', description: '', location: '', quantity: '' });
      fetchInventory();
    } catch (err) {
      console.error('Error adding item:', err);
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Inventory Items</h2>

      <h3>Add New Item</h3>
      <div style={styles.addForm}>
        <input
          type="text"
          placeholder="Item Name"
          value={formData.name}
          onChange={(e) => setFormData({ ...formData, name: e.target.value })}
          style={styles.input}
        />
        <input
          type="text"
          placeholder="Description"
          value={formData.description}
          onChange={(e) => setFormData({ ...formData, description: e.target.value })}
          style={styles.input}
        />
        <input
          type="text"
          placeholder="Location (e.g. A1)"
          value={formData.location}
          onChange={(e) => setFormData({ ...formData, location: e.target.value })}
          style={styles.input}
        />
        <input
          type="number"
          placeholder="Quantity"
          value={formData.quantity}
          onChange={(e) => setFormData({ ...formData, quantity: e.target.value })}
          style={styles.input}
        />
        <button onClick={handleAddItem} style={styles.saveBtn}>Add Item</button>
      </div>

      <table style={styles.table}>
        <thead>
          <tr>
            <th>Item Name</th>
            <th>Description</th>
            <th>Location</th>
            <th>Count</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {inventory.map(item => (
            <tr key={item._id}>
              <td>{item.name}</td>
              <td>{item.description || '—'}</td>
              <td>{item.location}</td>
              <td>{item.quantity}</td>
              <td>
                <button onClick={() => handleEditClick(item)} style={styles.actionBtn}>Edit</button>
                <button onClick={() => handleDelete(item._id)} style={{ ...styles.actionBtn, backgroundColor: '#dc3545' }}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editingItem && (
        <div style={styles.modal}>
          <div style={styles.modalContent}>
            <h3>Edit Item</h3>
            <input
              type="text"
              placeholder="Item Name"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              style={styles.input}
            />
            <input
              type="text"
              placeholder="Description"
              value={formData.description}
              onChange={(e) => setFormData({ ...formData, description: e.target.value })}
              style={styles.input}
            />
            <input
              type="text"
              placeholder="Location"
              value={formData.location}
              onChange={(e) => setFormData({ ...formData, location: e.target.value })}
              style={styles.input}
            />
            <input
              type="number"
              placeholder="Quantity"
              value={formData.quantity}
              onChange={(e) => setFormData({ ...formData, quantity: e.target.value })}
              style={styles.input}
            />
            <div style={{ display: 'flex', gap: '10px' }}>
              <button onClick={handleUpdate} style={styles.saveBtn}>Save</button>
              <button onClick={() => setEditingItem(null)} style={styles.cancelBtn}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    fontFamily: 'Arial, sans-serif',
    background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
    minHeight: '100vh',
    color: '#fff'
  },
  heading: {
    marginBottom: '20px',
    fontSize: '28px'
  },
  addForm: {
    marginBottom: '30px',
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(180px, 1fr))',
    gap: '10px',
    alignItems: 'center'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    backgroundColor: '#fff',
    boxShadow: '0px 2px 10px rgba(0,0,0,0.1)',
    color: '#000'
  },
  actionBtn: {
    marginRight: '5px',
    padding: '5px 10px',
    fontSize: '12px',
    borderRadius: '5px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    cursor: 'pointer'
  },
  modal: {
    position: 'fixed',
    top: 0, left: 0,
    width: '100%', height: '100%',
    backgroundColor: 'rgba(0,0,0,0.4)',
    display: 'flex', alignItems: 'center', justifyContent: 'center'
  },
  modalContent: {
    backgroundColor: '#fff',
    padding: '20px',
    borderRadius: '10px',
    width: '300px',
    boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
    color: '#000'
  },
  input: {
    width: '100%',
    padding: '8px',
    margin: '8px 0',
    borderRadius: '5px',
    border: '1px solid #ccc'
  },
  saveBtn: {
    padding: '8px 12px',
    backgroundColor: 'green',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer'
  },
  cancelBtn: {
    padding: '8px 12px',
    backgroundColor: '#888',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer'
  }
};

export default InventoryPage;
