// frontend/src/components/RequestPage.js
import React, { useState } from 'react';
import axios from 'axios';

const RequestPage = () => {
  const [itemName, setItemName] = useState('');
  const [quantity, setQuantity] = useState('');
  const [note, setNote] = useState('');
  const [message, setMessage] = useState('');

  const handleRequest = async (e) => {
    e.preventDefault();
    try {
      const user = JSON.parse(localStorage.getItem('user'));
      const res = await axios.post('http://localhost:5050/api/requests/add', {
        itemName,
        quantity,
        note,
        requestedBy: user.username
      });
      setMessage('Request submitted successfully!');
      setItemName('');
      setQuantity('');
      setNote('');
    } catch (err) {
      console.error(err);
      setMessage('Failed to submit request.');
    }
  };

  return (
    <div style={styles.container}>
      <h2>Request Items</h2>
      <form onSubmit={handleRequest} style={styles.form}>
        <input
          type="text"
          placeholder="Item Name"
          value={itemName}
          onChange={(e) => setItemName(e.target.value)}
          style={styles.input}
          required
        />
        <input
          type="number"
          placeholder="Quantity"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          style={styles.input}
          required
        />
        <textarea
          placeholder="Optional note"
          value={note}
          onChange={(e) => setNote(e.target.value)}
          style={{ ...styles.input, height: '60px' }}
        />
        <button type="submit" style={styles.button}>Submit Request</button>
        {message && <p style={styles.message}>{message}</p>}
      </form>
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    maxWidth: '500px',
    margin: 'auto'
  },
  form: {
    display: 'flex',
    flexDirection: 'column'
  },
  input: {
    padding: '10px',
    margin: '10px 0',
    borderRadius: '5px',
    border: '1px solid #ccc'
  },
  button: {
    padding: '10px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer'
  },
  message: {
    marginTop: '10px',
    fontWeight: 'bold'
  }
};

export default RequestPage;
