import React, { useEffect, useState } from 'react';
import axios from 'axios';

const InventoryPage = () => {
  const [items, setItems] = useState([]);
  const [mode, setMode] = useState('view');
  const [filterRack, setFilterRack] = useState('All');

  const [formName, setFormName] = useState('');
  const [formDescription, setFormDescription] = useState('');
  const [formLocation, setFormLocation] = useState('');
  const [formQuantity, setFormQuantity] = useState('');
  const [selectedItemId, setSelectedItemId] = useState(null);

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/inventory');
      setItems(res.data);
    } catch (err) {
      console.error('Error fetching inventory:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5050/api/inventory/delete/${id}`);
      fetchItems();
    } catch (err) {
      console.error('Error deleting item:', err);
    }
  };

  const handleEdit = (item) => {
    setSelectedItemId(item._id);
    setFormName(item.name);
    setFormDescription(item.description || '');
    setFormLocation(item.location || '');
    setFormQuantity(item.quantity || '');
  };

  const handleAddItem = async () => {
    try {
      await axios.post('http://localhost:5050/api/inventory/add', {
        name: formName,
        description: formDescription,
        location: formLocation,
        quantity: Number(formQuantity),
      });
      fetchItems();
      resetForm();
    } catch (err) {
      console.error('Error adding item:', err);
    }
  };

  const handleUpdateItem = async () => {
    console.log('Trying to update item:', selectedItemId);
    try {
      await axios.put(`http://localhost:5050/api/inventory/update/${selectedItemId}`, {
        name: formName,
        description: formDescription,
        location: formLocation,
        quantity: Number(formQuantity),
      });
      fetchItems();
      resetForm();
      setSelectedItemId(null);
    } catch (err) {
      console.error('Error updating item:', err);
    }
  };

  const resetForm = () => {
    setFormName('');
    setFormDescription('');
    setFormLocation('');
    setFormQuantity('');
    setSelectedItemId(null);
  };

  const handleModeToggle = (newMode) => {
    setMode(newMode);
    resetForm();
  };

  const handleFilterChange = (e) => {
    setFilterRack(e.target.value);
  };

  const groupByRack = (data) => {
    const grouped = {};
    data.forEach((item) => {
      const rack = item.location || 'Unknown';
      if (!grouped[rack]) grouped[rack] = [];
      grouped[rack].push(item);
    });
    return grouped;
  };

  const filteredItems = filterRack === 'All' ? items : items.filter(item => item.location === filterRack);
  const groupedItems = groupByRack(filteredItems);

  return (
    <div style={styles.page}>
      <h2 style={styles.heading}><span role="img" aria-label="box">📦</span> Inventory Items</h2>
      <div style={{ marginBottom: '15px' }}>
        <button onClick={() => handleModeToggle('view')} style={styles.button}>View Mode</button>
        <button onClick={() => handleModeToggle('update')} style={styles.button}>Update Mode</button>
      </div>

      {mode === 'view' && (
        <>
          <label style={{ color: 'white', marginRight: 10 }}>Filter by Rack:</label>
          <select onChange={handleFilterChange} value={filterRack} style={styles.dropdown}>
            <option value="All">All</option>
            {Array.from(new Set(items.map(item => item.location))).map(loc => (
              <option key={loc} value={loc}>{loc}</option>
            ))}
          </select>
          {Object.keys(groupedItems).sort().map((rack) => (
            <div key={rack} style={styles.rackSection}>
              <h3>📍 Rack {rack}</h3>
              <table style={styles.table}>
                <thead>
                  <tr>
                    <th>Item Name</th>
                    <th>Description</th>
                    <th>Count</th>
                  </tr>
                </thead>
                <tbody>
                  {groupedItems[rack].map((item) => (
                    <tr key={item._id}>
                      <td>{item.name}</td>
                      <td>{item.description || '—'}</td>
                      <td>{item.quantity || '—'}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </>
      )}

      {mode === 'update' && (
        <>
          <form
            onSubmit={(e) => {
              e.preventDefault();
              selectedItemId ? handleUpdateItem() : handleAddItem();
            }}
          >
            <input
              type="text"
              placeholder="Item Name"
              value={formName}
              onChange={(e) => setFormName(e.target.value)}
            />
            <input
              type="text"
              placeholder="Description"
              value={formDescription}
              onChange={(e) => setFormDescription(e.target.value)}
            />
            <input
              type="text"
              placeholder="Location (e.g., A1)"
              value={formLocation}
              onChange={(e) => setFormLocation(e.target.value)}
            />
            <input
              type="number"
              placeholder="Quantity"
              value={formQuantity}
              onChange={(e) => setFormQuantity(e.target.value)}
            />
            <button type="submit" style={styles.editBtn}>
              {selectedItemId ? 'Update Item' : 'Add Item'}
            </button>
            {selectedItemId && (
              <button
                type="button"
                onClick={resetForm}
                style={styles.deleteBtn}
              >
                Cancel
              </button>
            )}
          </form>

          <table style={styles.table}>
            <thead>
              <tr>
                <th>Item Name</th>
                <th>Description</th>
                <th>Location</th>
                <th>Count</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {items.map((item) => (
                <tr key={item._id}>
                  <td>{item.name}</td>
                  <td>{item.description || '—'}</td>
                  <td>{item.location || '—'}</td>
                  <td>{item.quantity || '—'}</td>
                  <td>
                    <button onClick={() => handleEdit(item)} style={styles.editBtn}>Edit</button>
                    <button onClick={() => handleDelete(item._id)} style={styles.deleteBtn}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
};

const styles = {
  page: {
    padding: '30px',
    background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
    minHeight: '100vh',
    color: '#fff',
  },
  heading: {
    marginBottom: '20px'
  },
  button: {
    padding: '8px 14px',
    marginRight: '10px',
    backgroundColor: '#fff',
    color: '#000',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer'
  },
  dropdown: {
    padding: '6px 12px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    marginBottom: '20px'
  },
  rackSection: {
    backgroundColor: '#f9f9f9',
    color: '#000',
    borderRadius: '10px',
    padding: '20px',
    marginBottom: '20px'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    backgroundColor: '#fff',
    color: '#000',
    borderRadius: '8px',
    overflow: 'hidden'
  },
  editBtn: {
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    padding: '6px 10px',
    borderRadius: '4px',
    marginRight: '5px',
    cursor: 'pointer'
  },
  deleteBtn: {
    backgroundColor: '#dc3545',
    color: '#fff',
    border: 'none',
    padding: '6px 10px',
    borderRadius: '4px',
    cursor: 'pointer'
  }
};

export default InventoryPage;
