// src/pages/ApprovePage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ApprovePage = () => {
  const [requests, setRequests] = useState([]);
  const [message, setMessage] = useState('');

  const fetchRequests = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/requests');
      setRequests(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const approveRequest = async (id) => {
    try {
      const res = await axios.put(`http://localhost:5050/api/requests/approve/${id}`);
      setMessage(res.data.message);
      fetchRequests(); // refresh the list
    } catch (err) {
      console.error(err);
      setMessage('Failed to approve request');
    }
  };

  useEffect(() => {
    fetchRequests();
  }, []);

  return (
    <div style={styles.container}>
      <h2>Approve Requests</h2>
      {message && <p style={styles.message}>{message}</p>}

      {requests.length === 0 ? (
        <p>No requests found.</p>
      ) : (
        <ul style={styles.list}>
          {requests.map((req) => (
            <li key={req._id} style={styles.card}>
              <strong>{req.itemName}</strong> ({req.quantity})<br />
              Note: {req.note || 'N/A'}<br />
              Requested by: {req.requestedBy}<br />
              Status: <span style={{ fontWeight: 'bold' }}>{req.status}</span><br />
              {req.status === 'Pending' && (
                <button onClick={() => approveRequest(req._id)} style={styles.button}>
                  Approve
                </button>
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '40px',
    fontFamily: 'Arial, sans-serif'
  },
  list: {
    listStyleType: 'none',
    padding: 0
  },
  card: {
    backgroundColor: '#fff',
    padding: '15px',
    marginBottom: '15px',
    borderRadius: '8px',
    boxShadow: '0 2px 5px rgba(0,0,0,0.1)'
  },
  button: {
    marginTop: '10px',
    padding: '8px 12px',
    border: 'none',
    borderRadius: '5px',
    backgroundColor: '#28a745',
    color: '#fff',
    cursor: 'pointer'
  },
  message: {
    marginBottom: '10px',
    color: 'green'
  }
};

export default ApprovePage;
