import React from 'react';
import { useNavigate } from 'react-router-dom';

const InventoryManagerDashboard = () => {
  const user = JSON.parse(localStorage.getItem('user'));
  const navigate = useNavigate();

  return (
    <div style={styles.container}>
      <h2>Welcome, {user?.username || 'Inventory Manager'}!</h2>
      <p>Role: <strong>{user?.role}</strong></p>

      <div style={styles.cardContainer}>
        {/* Manage Inventory */}
        <div style={styles.card}>
          <h3>ðŸ“¦ Manage Inventory</h3>
          <p>View and update existing stock.</p>
          <button onClick={() => navigate('/inventory')} style={styles.goBtn}>Go</button>
        </div>

        {/* Fulfill Requests */}
        <div style={styles.card}>
          <h3>ðŸ“¬ Fulfill Requests</h3>
          <p>Process item requests from STEMbassadors.</p>
          <button onClick={() => navigate('/approve')} style={styles.goBtn}>Go</button>
        </div>

        {/* Add New Item */}
        <div style={styles.card}>
          <h3>âž• Add New Item</h3>
          <p>Add new items to the inventory system.</p>
          <button onClick={() => navigate('/inventory/add')} style={styles.goBtn}>Go</button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '40px',
    background: 'linear-gradient(to right, #e0f7fa, #e0ffe0)',
    fontFamily: 'Arial',
    minHeight: '100vh'
  },
  cardContainer: {
    display: 'flex',
    gap: '30px',
    marginTop: '30px',
    flexWrap: 'wrap'
  },
  card: {
    flex: '1',
    minWidth: '250px',
    backgroundColor: '#fff',
    padding: '25px',
    borderRadius: '12px',
    boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)'
  },
  goBtn: {
    marginTop: '10px',
    padding: '8px 16px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '6px',
    cursor: 'pointer'
  }
};

export default InventoryManagerDashboard;
