// src/components/LoginPage.js
import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const API_BASE = 'https://vercel-backend-rosy-sigma.vercel.app';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await axios.post(`${API_BASE}/api/auth/login`, {
        username,
        password
      });

      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));

      const role = res.data.user.role;
      if (role === 'director') {
        window.location.href = '/dashboard/director';
      } else if (role === 'inventoryManager') {
        window.location.href = '/dashboard/inventory';
      } else if (role === 'stembassador') {
        window.location.href = '/dashboard/stembassador';
      } else {
        setError('Unknown role. Contact admin.');
      }
    } catch (err) {
      const msg =
        err.response?.data?.message ||
        'Login failed. Please check your credentials.';
      setError(msg);
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.overlay}>
        <div style={styles.card}>
          <img
            src="/pe-logo.jpeg"
            alt="Project Exploration Logo"
            style={{ width: '80px', marginBottom: '20px' }}
          />

          <h1 style={styles.title}>üîê Project Exploration</h1>
          <p style={styles.subtitle}>Sign in to access the inventory portal</p>

          <form onSubmit={handleLogin}>
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              style={styles.input}
              required
            />

            <div style={styles.passwordWrapper}>
              <input
                type={showPassword ? 'text' : 'password'}
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                style={styles.input}
                required
              />
              <span
                onClick={() => setShowPassword(!showPassword)}
                style={styles.toggle}
              >
                {showPassword ? 'Hide' : 'Show'}
              </span>
            </div>

            <div style={styles.linkContainer}>
              <Link to="/forgot-password" style={styles.link}>
                Forgot Password?
              </Link>
            </div>

            <button type="submit" style={styles.button}>
              Login
            </button>

            {error && <p style={styles.error}>{error}</p>}

            <div style={styles.signupLink}>
              <span>New here? </span>
              <Link to="/signup" style={styles.link}>
                Create an account
              </Link>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    height: '100vh',
    background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontFamily: 'Arial, sans-serif'
  },
  overlay: {
    backdropFilter: 'blur(10px)',
    backgroundColor: 'rgba(255, 255, 255, 0.65)',
    padding: '40px',
    borderRadius: '20px',
    boxShadow: '0 8px 24px rgba(0, 0, 0, 0.2)'
  },
  card: {
    width: '360px',
    backgroundColor: '#ffffff',
    borderRadius: '16px',
    padding: '40px 30px',
    boxShadow: '0 10px 30px rgba(0, 0, 0, 0.2)',
    textAlign: 'center'
  },
  title: {
    fontSize: '26px',
    marginBottom: '10px',
    fontWeight: 'bold',
    color: '#2c3e50'
  },
  subtitle: {
    fontSize: '15px',
    color: '#555',
    marginBottom: '25px'
  },
  input: {
    width: '100%',
    padding: '12px',
    marginBottom: '15px',
    borderRadius: '8px',
    border: '1px solid #ccc',
    fontSize: '14px',
    outline: 'none'
  },
  passwordWrapper: {
    position: 'relative'
  },
  toggle: {
    position: 'absolute',
    top: '50%',
    right: '12px',
    transform: 'translateY(-50%)',
    fontSize: '12px',
    color: '#007bff',
    cursor: 'pointer'
  },
  linkContainer: {
    textAlign: 'right',
    marginBottom: '10px'
  },
  link: {
    fontSize: '12px',
    color: '#007bff',
    textDecoration: 'none',
    cursor: 'pointer'
  },
  button: {
    width: '100%',
    padding: '12px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '8px',
    fontWeight: 'bold',
    fontSize: '15px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease'
  },
  error: {
    color: 'red',
    marginTop: '12px',
    fontSize: '13px'
  },
  signupLink: {
    marginTop: '15px',
    fontSize: '13px',
    color: '#333'
  }
};

export default LoginPage;
