import React, { useEffect, useState } from 'react';
import axios from 'axios';

const InventorySummaryPage = () => {
  const [items, setItems] = useState([]);

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/inventory/all');
      setItems(res.data);
    } catch (err) {
      console.error('Error fetching inventory:', err);
    }
  };

  const groupByRack = () => {
    const grouped = {};

    items.forEach((item) => {
      const rackLetter = item.location?.[0]?.toUpperCase() || 'Unknown';
      if (!grouped[rackLetter]) {
        grouped[rackLetter] = [];
      }
      grouped[rackLetter].push(item);
    });

    return grouped;
  };

  const groupedItems = groupByRack();

  return (
    <div style={styles.container}>
      <h2>ðŸ“¦ Inventory Summary by Rack</h2>
      {Object.keys(groupedItems).sort().map((rack) => (
        <div key={rack} style={styles.rackSection}>
          <h3 style={styles.rackTitle}>Rack {rack}</h3>
          <table style={styles.table}>
            <thead>
              <tr>
                <th>Item</th>
                <th>Description</th>
                <th>Location</th>
                <th>Count</th>
              </tr>
            </thead>
            <tbody>
              {groupedItems[rack].map((item) => (
                <tr key={item._id}>
                  <td>{item.name}</td>
                  <td>{item.description || 'â€”'}</td>
                  <td>{item.location}</td>
                  <td>{item.count}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    fontFamily: 'Arial, sans-serif',
    background: 'linear-gradient(to right, #e0f7fa, #fdfdfd)',
    minHeight: '100vh'
  },
  rackSection: {
    marginBottom: '40px',
    backgroundColor: '#fff',
    padding: '15px',
    borderRadius: '10px',
    boxShadow: '0 0 8px rgba(0,0,0,0.08)'
  },
  rackTitle: {
    marginBottom: '10px',
    color: '#333'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse'
  }
};

export default InventorySummaryPage;
