import React, { useEffect, useState } from 'react';
import axios from 'axios';

const InventoryPage = () => {
  const [inventory, setInventory] = useState([]);
  const [viewMode, setViewMode] = useState('update');
  const [showAddModal, setShowAddModal] = useState(false);
  const [newItem, setNewItem] = useState({ name: '', description: '', location: '', count: '' });

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/inventory/');
      console.log("Fetched Inventory:", res.data);
      setInventory(res.data);
    } catch (err) {
      console.error('Error fetching inventory:', err);
    }
  };

  const groupByRack = (items) => {
    const grouped = {};
    items.forEach(item => {
      const rack = item.location && item.location.trim() !== '' 
        ? item.location.toUpperCase() 
        : 'Unknown Rack';
      if (!grouped[rack]) grouped[rack] = [];
      grouped[rack].push(item);
    });
    console.log("Grouped Inventory:", grouped);
    return grouped;
  };

  const handleAddItem = async () => {
    try {
      await axios.post('http://localhost:5050/api/inventory/add', newItem);
      setShowAddModal(false);
      setNewItem({ name: '', description: '', location: '', count: '' });
      fetchInventory();
    } catch (err) {
      console.error('Error adding item:', err);
    }
  };

  return (
    <div style={styles.container}>
      <h2>üì¶ Inventory Items</h2>
      <div style={styles.toggleBtns}>
        <button onClick={() => setViewMode('view')} style={styles.toggleBtn}>View Mode</button>
        <button onClick={() => setViewMode('update')} style={styles.toggleBtn}>Update Mode</button>
      </div>

      {viewMode === 'view' ? (
        Object.keys(groupByRack(inventory)).length === 0 ? (
          <p>No items available to display.</p>
        ) : (
          Object.entries(groupByRack(inventory)).map(([rack, items]) => (
            <div key={rack} style={styles.rackGroup}>
              <h3>üìç Rack {rack}</h3>
              <table style={styles.table}>
                <thead>
                  <tr>
                    <th>Item Name</th>
                    <th>Description</th>
                    <th>Count</th>
                  </tr>
                </thead>
                <tbody>
                  {items.map(item => (
                    <tr key={item._id}>
                      <td>{item.name}</td>
                      <td>{item.description || '‚Äî'}</td>
                      <td>{item.count}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))
        )
      ) : (
        <>
          <button style={styles.addBtn} onClick={() => setShowAddModal(true)}>‚ûï Add New Item</button>
          <table style={styles.table}>
            <thead>
              <tr>
                <th>Item Name</th>
                <th>Description</th>
                <th>Location</th>
                <th>Count</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {inventory.map(item => (
                <tr key={item._id}>
                  <td>{item.name}</td>
                  <td>{item.description || '‚Äî'}</td>
                  <td>{item.location}</td>
                  <td>{item.count}</td>
                  <td>
                    <button style={styles.editBtn}>Edit</button>
                    <button style={styles.deleteBtn}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}

      {showAddModal && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <h3>Add New Item</h3>
            <input placeholder="Item Name" value={newItem.name} onChange={e => setNewItem({ ...newItem, name: e.target.value })} />
            <input placeholder="Description" value={newItem.description} onChange={e => setNewItem({ ...newItem, description: e.target.value })} />
            <input placeholder="Location" value={newItem.location} onChange={e => setNewItem({ ...newItem, location: e.target.value })} />
            <input placeholder="Count" type="number" value={newItem.count} onChange={e => setNewItem({ ...newItem, count: e.target.value })} />
            <div style={{ marginTop: '10px' }}>
              <button onClick={handleAddItem} style={styles.editBtn}>Add</button>
              <button onClick={() => setShowAddModal(false)} style={styles.deleteBtn}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
    minHeight: '100vh',
    color: 'white'
  },
  toggleBtns: {
    marginBottom: '20px',
    display: 'flex',
    gap: '10px'
  },
  toggleBtn: {
    backgroundColor: '#1e90ff',
    border: 'none',
    color: 'white',
    padding: '8px 16px',
    borderRadius: '6px',
    cursor: 'pointer',
    fontWeight: 'bold'
  },
  addBtn: {
    backgroundColor: '#28a745',
    color: 'white',
    padding: '8px 16px',
    border: 'none',
    borderRadius: '6px',
    cursor: 'pointer',
    marginBottom: '15px'
  },
  rackGroup: {
    backgroundColor: 'white',
    color: 'black',
    padding: '10px',
    borderRadius: '8px',
    marginBottom: '20px'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    marginTop: '10px',
    backgroundColor: '#fff',
    color: '#000'
  },
  editBtn: {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '6px 10px',
    marginRight: '5px',
    border: 'none',
    borderRadius: '4px'
  },
  deleteBtn: {
    backgroundColor: '#dc3545',
    color: 'white',
    padding: '6px 10px',
    border: 'none',
    borderRadius: '4px'
  },
  modalOverlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100vw',
    height: '100vh',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    zIndex: 999
  },
  modalContent: {
    backgroundColor: 'white',
    color: 'black',
    padding: '20px',
    borderRadius: '8px',
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
    width: '300px'
  }
};

export default InventoryPage;
