import React, { useEffect, useState } from 'react';
import axios from 'axios';

const InventoryPage = () => {
  const [inventory, setInventory] = useState([]);
  const [viewMode, setViewMode] = useState('update'); // 'view' or 'update'
  const [showAddForm, setShowAddForm] = useState(false);
  const [newItem, setNewItem] = useState({
    name: '',
    description: '',
    location: '',
    count: ''
  });

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/inventory/');
      setInventory(res.data);
    } catch (err) {
      console.error('Error fetching inventory:', err);
    }
  };

  const groupByRack = (items) => {
    const grouped = {};
    items.forEach(item => {
      const rack = item.location && item.location.trim() !== ''
        ? item.location.toUpperCase()
        : 'Unknown Rack';
      if (!grouped[rack]) grouped[rack] = [];
      grouped[rack].push(item);
    });
    return grouped;
  };

  const handleAdd = async () => {
    try {
      await axios.post('http://localhost:5050/api/inventory/add', newItem);
      setNewItem({ name: '', description: '', location: '', count: '' });
      setShowAddForm(false);
      fetchInventory();
    } catch (err) {
      console.error('Error adding item:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5050/api/inventory/delete/${id}`);
      fetchInventory();
    } catch (err) {
      console.error('Error deleting item:', err);
    }
  };

  const handleCancelAdd = () => {
    setShowAddForm(false);
    setNewItem({ name: '', description: '', location: '', count: '' });
  };

  return (
    <div style={styles.container}>
      <h2>📦 Inventory Items</h2>
      <div style={styles.toggleBtns}>
        <button onClick={() => setViewMode('view')} style={styles.toggleBtn}>View Mode</button>
        <button onClick={() => setViewMode('update')} style={styles.toggleBtn}>Update Mode</button>
      </div>

      {viewMode === 'update' && !showAddForm && (
        <button onClick={() => setShowAddForm(true)} style={styles.addBtn}>+ Add New Item</button>
      )}

      {showAddForm ? (
        <div style={styles.addFormWrapper}>
          <div style={styles.addForm}>
            <h3>Add New Item</h3>
            <input
              placeholder="Item Name"
              value={newItem.name}
              onChange={e => setNewItem({ ...newItem, name: e.target.value })}
              style={styles.input}
            />
            <input
              placeholder="Description"
              value={newItem.description}
              onChange={e => setNewItem({ ...newItem, description: e.target.value })}
              style={styles.input}
            />
            <input
              placeholder="Location"
              value={newItem.location}
              onChange={e => setNewItem({ ...newItem, location: e.target.value })}
              style={styles.input}
            />
            <input
              placeholder="Count"
              type="number"
              value={newItem.count}
              onChange={e => setNewItem({ ...newItem, count: e.target.value })}
              style={styles.input}
            />
            <div style={styles.addFormButtons}>
              <button onClick={handleAdd} style={styles.editBtn}>Add</button>
              <button onClick={handleCancelAdd} style={styles.deleteBtn}>Cancel</button>
            </div>
          </div>
        </div>
      ) : (
        viewMode === 'view' ? (
          Object.keys(groupByRack(inventory)).length === 0 ? (
            <p>No items available to display.</p>
          ) : (
            Object.entries(groupByRack(inventory)).map(([rack, items]) => (
              <div key={rack} style={styles.rackGroup}>
                <h3>📍 Rack {rack}</h3>
                <table style={styles.table}>
                  <thead>
                    <tr>
                      <th>Item Name</th>
                      <th>Description</th>
                      <th>Count</th>
                    </tr>
                  </thead>
                  <tbody>
                    {items.map(item => (
                      <tr key={item._id}>
                        <td>{item.name}</td>
                        <td>{item.description || '—'}</td>
                        <td>{item.count}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ))
          )
        ) : (
          <table style={styles.table}>
            <thead>
              <tr>
                <th>Item Name</th>
                <th>Description</th>
                <th>Location</th>
                <th>Count</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {inventory.map(item => (
                <tr key={item._id}>
                  <td>{item.name}</td>
                  <td>{item.description || '—'}</td>
                  <td>{item.location}</td>
                  <td>{item.count}</td>
                  <td>
                    <button style={styles.editBtn}>Edit</button>
                    <button onClick={() => handleDelete(item._id)} style={styles.deleteBtn}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
    minHeight: '100vh',
    color: 'white'
  },
  toggleBtns: {
    marginBottom: '20px',
    display: 'flex',
    gap: '10px'
  },
  toggleBtn: {
    backgroundColor: '#1e90ff',
    border: 'none',
    color: 'white',
    padding: '8px 16px',
    borderRadius: '6px',
    cursor: 'pointer',
    fontWeight: 'bold'
  },
  addBtn: {
    backgroundColor: '#28a745',
    border: 'none',
    color: 'white',
    padding: '8px 16px',
    borderRadius: '6px',
    cursor: 'pointer',
    fontWeight: 'bold',
    marginBottom: '20px'
  },
  rackGroup: {
    backgroundColor: 'white',
    color: 'black',
    padding: '10px',
    borderRadius: '8px',
    marginBottom: '20px'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    marginTop: '10px',
    backgroundColor: '#fff',
    color: '#000'
  },
  editBtn: {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '6px 10px',
    marginRight: '5px',
    border: 'none',
    borderRadius: '4px'
  },
  deleteBtn: {
    backgroundColor: '#dc3545',
    color: 'white',
    padding: '6px 10px',
    border: 'none',
    borderRadius: '4px'
  },
  addFormWrapper: {
    display: 'flex',
    justifyContent: 'center',
    marginTop: '40px'
  },
  addForm: {
    backgroundColor: '#fff',
    padding: '20px',
    borderRadius: '10px',
    width: '350px',
    boxShadow: '0 0 10px rgba(0,0,0,0.2)',
    color: '#000'
  },
  input: {
    width: '100%',
    padding: '8px',
    margin: '6px 0',
    borderRadius: '4px',
    border: '1px solid #ccc'
  },
  addFormButtons: {
    display: 'flex',
    justifyContent: 'flex-start',
    gap: '10px',
    marginTop: '10px'
  }
};

export default InventoryPage;
