import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ApprovePage = () => {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    fetchRequests();
  }, []);

  const fetchRequests = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/requests');
      setRequests(res.data);
    } catch (err) {
      console.error('Failed to fetch requests:', err);
    }
  };

  const updateStatus = async (id, newStatus) => {
    try {
      await axios.put(`http://localhost:5050/api/requests/${id}`, { status: newStatus });
      fetchRequests(); // Refresh the list after update
    } catch (err) {
      console.error('Failed to update request:', err);
    }
  };

  return (
    <div style={styles.container}>
      <h2>✅ Review Item Requests</h2>
      {requests.length === 0 ? (
        <p>No requests found.</p>
      ) : (
        <table style={styles.table}>
          <thead>
            <tr>
              <th>Item</th>
              <th>Quantity</th>
              <th>Note</th>
              <th>Requested By</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {requests.map((req) => (
              <tr key={req._id}>
                <td>{req.itemName}</td>
                <td>{req.quantity}</td>
                <td>{req.note || '—'}</td>
                <td>{req.requestedBy}</td>
                <td>{req.status}</td>
                <td>
                  {req.status === 'Pending' ? (
                    <>
                      <button
                        style={{ ...styles.btn, backgroundColor: '#28a745' }}
                        onClick={() => updateStatus(req._id, 'Approved')}
                      >
                        Approve
                      </button>
                      <button
                        style={{ ...styles.btn, backgroundColor: '#dc3545' }}
                        onClick={() => updateStatus(req._id, 'Rejected')}
                      >
                        Reject
                      </button>
                    </>
                  ) : (
                    <span style={{ color: req.status === 'Approved' ? 'green' : 'red' }}>
                      {req.status}
                    </span>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    fontFamily: 'Arial, sans-serif',
    background: 'linear-gradient(to right, #fdfbfb, #ebedee)',
    minHeight: '100vh'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    marginTop: '20px',
    backgroundColor: '#fff',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)'
  },
  btn: {
    color: 'white',
    border: 'none',
    padding: '6px 10px',
    marginRight: '5px',
    borderRadius: '4px',
    cursor: 'pointer'
  }
};

export default ApprovePage;
