import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RequestItemsPage = () => {
  const [inventory, setInventory] = useState([]);
  const [selectedItem, setSelectedItem] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [requests, setRequests] = useState([]);
  const [message, setMessage] = useState('');

  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchInventory = async () => {
      try {
        const res = await axios.get('http://localhost:5050/api/inventory');
        setInventory(res.data);
      } catch (err) {
        console.error('Error fetching inventory:', err);
      }
    };

    fetchInventory();
  }, []);

  const addRequest = () => {
    if (!selectedItem || quantity < 1) return;
    const item = inventory.find(i => i._id === selectedItem);
    setRequests(prev => [...prev, { itemId: selectedItem, name: item.name, quantity }]);
    setQuantity(1);
    setSelectedItem('');
  };

  const submitRequests = async () => {
    try {
      await axios.post('http://localhost:5050/api/requests', {
        requests: requests.map(({ itemId, quantity }) => ({ itemId, quantity })),
        requestedBy: user.username,
      });
      setMessage('Request submitted successfully!');
      setRequests([]);
    } catch (err) {
      console.error('Error submitting request:', err);
      setMessage('Failed to submit request.');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-900 to-blue-700 text-white p-6">
      <h1 className="text-3xl font-bold mb-4">üìö Request Inventory Items</h1>

      <div className="mb-6 flex gap-4">
        <select
          value={selectedItem}
          onChange={e => setSelectedItem(e.target.value)}
          className="text-black p-2 rounded"
        >
          <option value="">Select Item</option>
          {inventory.map(item => (
            <option key={item._id} value={item._id}>
              {item.name} (Available: {item.count})
            </option>
          ))}
        </select>

        <input
          type="number"
          value={quantity}
          min="1"
          onChange={e => setQuantity(Number(e.target.value))}
          className="text-black p-2 rounded w-24"
        />

        <button onClick={addRequest} className="bg-green-500 hover:bg-green-600 px-4 py-2 rounded">
          Add
        </button>
      </div>

      {requests.length > 0 ? (
        <div className="mb-4">
          <h2 className="font-semibold mb-2">üìù Items to Request:</h2>
          <ul className="list-disc ml-5">
            {requests.map((r, idx) => (
              <li key={idx}>
                {r.name} ‚Äî Quantity: {r.quantity}
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>No items added.</p>
      )}

      <button
        onClick={submitRequests}
        disabled={requests.length === 0}
        className="bg-green-600 hover:bg-green-700 px-6 py-2 rounded mt-4"
      >
        Submit Request
      </button>

      {message && <p className="mt-4 text-yellow-300">{message}</p>}
    </div>
  );
};

export default RequestItemsPage;
