import React from 'react';
import { useNavigate } from 'react-router-dom';

function StembassadorDashboard() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));

  return (
    <div className="min-h-screen bg-gradient-to-r from-[#0f2027] via-[#203a43] to-[#2c5364] flex flex-col items-center justify-center p-6">
      <div className="text-center text-white mb-10">
        <h1 className="text-4xl font-bold mb-2">Welcome, {user?.username}!</h1>
        <p className="text-xl">Role: <span className="font-semibold">stembassador</span></p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        <div className="bg-white rounded-2xl shadow-lg p-8 w-80 text-center">
          <h3 className="text-xl font-bold mb-2">üì¶ View Inventory</h3>
          <p className="mb-4 text-gray-700">Browse and manage items in stock.</p>
          <button
            onClick={() => navigate('/inventory')}
            className="bg-teal-800 hover:bg-teal-900 text-white px-6 py-2 rounded-lg transition"
          >
            Go
          </button>
        </div>

        <div className="bg-white rounded-2xl shadow-lg p-8 w-80 text-center">
          <h3 className="text-xl font-bold mb-2">üìù Request Items</h3>
          <p className="mb-4 text-gray-700">Request items for events or programs.</p>
          <button
            onClick={() => navigate('/stembassador/request')}
            className="bg-teal-800 hover:bg-teal-900 text-white px-6 py-2 rounded-lg transition"
          >
            Go
          </button>
        </div>
      </div>

      <button
        onClick={() => {
          localStorage.removeItem('user');
          navigate('/');
        }}
        className="mt-10 bg-red-500 hover:bg-red-600 text-white px-6 py-2 rounded-lg transition"
      >
        Logout
      </button>
    </div>
  );
}

export default StembassadorDashboard;
