import React, { useEffect, useState } from 'react';
import axios from 'axios';

const FulfillRequestsPage = () => {
  const [requests, setRequests] = useState([]);
  const [message, setMessage] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;

  useEffect(() => {
    fetchRequests();
  }, []);

  const fetchRequests = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/requests');
      setRequests(res.data);
    } catch (err) {
      console.error('Error fetching requests:', err);
    }
  };

  const handleAction = async (id, action) => {
    try {
      await axios.put(`http://localhost:5050/api/requests/${action}/${id}`);
      setMessage(`Request ${action}ed successfully.`);
      fetchRequests();
    } catch (err) {
      console.error(`Error trying to ${action} request:`, err);
      setMessage(`Failed to ${action} request.`);
    }
  };

  const handleExport = () => {
    const fulfilled = requests.filter(req => req.status === 'approved' || req.status === 'rejected');
    const csvContent =
      'data:text/csv;charset=utf-8,' +
      ['Item,Quantity,Requested By,Status']
        .concat(
          fulfilled.map(
            req =>
              `${req.itemName || req.itemId?.name || 'Unknown'},${req.quantity},${req.requestedBy},${req.status}`
          )
        )
        .join('\n');
    const encodedUri = encodeURI(csvContent);
    const link = document.createElement('a');
    link.setAttribute('href', encodedUri);
    link.setAttribute('download', 'fulfilled_requests.csv');
    document.body.appendChild(link);
    link.click();
  };

  const filteredRequests = requests.filter(
    req =>
      (req.itemName || req.itemId?.name || '')
        .toLowerCase()
        .includes(searchTerm.toLowerCase()) ||
      req.requestedBy.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const totalPages = Math.ceil(filteredRequests.length / itemsPerPage);
  const displayedRequests = filteredRequests.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  const approvedCount = requests.filter(req => req.status === 'approved').length;
  const rejectedCount = requests.filter(req => req.status === 'rejected').length;
  const pendingCount = requests.filter(req => req.status === 'pending').length;

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>üì¨ Fulfill Requests</h2>

      <div style={styles.summaryBox}>
        <strong>Summary:</strong> Total: {requests.length} | ‚úÖ Approved: {approvedCount} | ‚ùå Rejected: {rejectedCount} | ‚è≥ Pending: {pendingCount}
      </div>

      <input
        type="text"
        placeholder="Search by item or user..."
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
        style={styles.searchBar}
      />
      <button onClick={handleExport} style={styles.exportBtn}>Export</button>

      {message && <p style={styles.message}>{message}</p>}

      {filteredRequests.length === 0 ? (
        <p>No requests found.</p>
      ) : (
        <table style={styles.table}>
          <thead>
            <tr>
              <th>Item</th>
              <th>Quantity</th>
              <th>Requested By</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {displayedRequests.map((req) => (
              <tr key={req._id}>
                <td>{req.itemName || req.itemId?.name || 'Unknown'}</td>
                <td>{req.quantity}</td>
                <td>{req.requestedBy}</td>
                <td>
                  <button
                    onClick={() => handleAction(req._id, 'approve')}
                    style={styles.approveBtn}
                  >
                    Approve
                  </button>
                  <button
                    onClick={() => handleAction(req._id, 'reject')}
                    style={styles.rejectBtn}
                  >
                    Reject
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* Pagination Controls */}
      <div style={styles.pagination}>
        {Array.from({ length: totalPages }, (_, idx) => (
          <button
            key={idx}
            onClick={() => setCurrentPage(idx + 1)}
            style={{
              ...styles.pageButton,
              backgroundColor: currentPage === idx + 1 ? '#007bff' : '#f0f0f0',
              color: currentPage === idx + 1 ? '#fff' : '#000'
            }}
          >
            {idx + 1}
          </button>
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    fontFamily: 'Arial, sans-serif',
    background: 'linear-gradient(to right, #1e3c48, #274b57)',
    minHeight: '100vh',
    color: '#fff'
  },
  heading: {
    color: '#ffffff'
  },
  summaryBox: {
    backgroundColor: '#0f5d85',
    padding: '10px',
    borderRadius: '8px',
    margin: '1rem 0',
    color: '#fff'
  },
  searchBar: {
    padding: '8px',
    borderRadius: '6px',
    border: '1px solid #ccc',
    marginRight: '10px',
    width: '250px',
    marginBottom: '10px'
  },
  exportBtn: {
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    padding: '8px 14px',
    borderRadius: '6px',
    cursor: 'pointer'
  },
  message: {
    padding: '10px',
    marginTop: '10px',
    backgroundColor: '#d4edda',
    color: '#155724',
    border: '1px solid #c3e6cb',
    borderRadius: '5px',
    fontWeight: 'bold'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    marginTop: '20px',
    backgroundColor: '#fff',
    color: '#000',
    boxShadow: '0 0 8px rgba(0,0,0,0.1)'
  },
  approveBtn: {
    backgroundColor: '#007bff',
    color: '#fff',
    padding: '6px 12px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    marginRight: '5px'
  },
  rejectBtn: {
    backgroundColor: '#dc3545',
    color: '#fff',
    padding: '6px 12px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer'
  },
  pagination: {
    marginTop: '20px'
  },
  pageButton: {
    padding: '6px 12px',
    border: '1px solid #ccc',
    borderRadius: '4px',
    margin: '0 5px',
    cursor: 'pointer'
  }
};

export default FulfillRequestsPage;
