import React, { useEffect, useState } from 'react';
import axios from 'axios';

const FulfillRequestsPage = () => {
  const [requests, setRequests] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchRequests();
  }, []);

  const fetchRequests = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/requests/pending');
      setRequests(res.data);
    } catch (err) {
      console.error('Error fetching requests:', err);
    }
  };

  const handleAction = async (id, action) => {
    try {
      const res = await axios.put(`http://localhost:5050/api/requests/${action}/${id}`);
      setMessage(`Request ${action}ed successfully.`);
      fetchRequests();
    } catch (err) {
      console.error(`Error trying to ${action} request:`, err);
      setMessage(`Failed to ${action} request.`);
    }
  };

  return (
    <div style={styles.container}>
      <h2>ðŸ“¬ Fulfill Requests</h2>
      {message && <p style={styles.message}>{message}</p>}

      {requests.length === 0 ? (
        <p>No pending requests found.</p>
      ) : (
        <table style={styles.table}>
          <thead>
            <tr>
              <th>Item</th>
              <th>Quantity</th>
              <th>Requested By</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {requests.map((req) => (
              <tr key={req._id}>
                <td>{req.itemId?.name || req.itemName || 'Unknown'}</td>
                <td>{req.quantity}</td>
                <td>{req.requestedBy || 'STEMbassador'}</td>
                <td>
                  <button onClick={() => handleAction(req._id, 'approve')} style={styles.approveBtn}>Approve</button>
                  <button onClick={() => handleAction(req._id, 'reject')} style={styles.rejectBtn}>Reject</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    fontFamily: 'Arial',
    background: 'linear-gradient(to right, #fdfbfb, #ebedee)',
    minHeight: '100vh'
  },
  message: {
    padding: '10px',
    marginBottom: '15px',
    backgroundColor: '#e9f7ef',
    color: '#155724',
    border: '1px solid #c3e6cb',
    borderRadius: '5px',
    fontWeight: 'bold'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    marginTop: '20px',
    backgroundColor: '#fff',
    boxShadow: '0 0 8px rgba(0,0,0,0.1)'
  },
  approveBtn: {
    backgroundColor: '#28a745',
    color: '#fff',
    padding: '6px 10px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    marginRight: '5px'
  },
  rejectBtn: {
    backgroundColor: '#dc3545',
    color: '#fff',
    padding: '6px 10px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer'
  }
};

export default FulfillRequestsPage;
