import React, { useEffect, useState } from 'react';
import axios from 'axios';

const InventorySummaryPage = () => {
  const [items, setItems] = useState([]);

  useEffect(() => {
    const fetchInventory = async () => {
      try {
        const res = await axios.get('http://localhost:5050/api/inventory');
        setItems(res.data);
      } catch (err) {
        console.error('Failed to fetch inventory:', err);
      }
    };
    fetchInventory();
  }, []);

  return (
    <div style={styles.container}>
      <h2>ðŸ“¦ Inventory Summary</h2>

      <div style={styles.summaryBox}>
        <p>Total Items: {items.length}</p>
        <p>Low Stock: {items.filter(item => item.quantity <= 3).length}</p>
      </div>

      <table style={styles.table}>
        <thead>
          <tr>
            <th>Item</th>
            <th>Description</th>
            <th>Location</th>
            <th>Quantity</th>
          </tr>
        </thead>
        <tbody>
          {items.map(item => (
            <tr
              key={item._id}
              style={item.quantity <= 3 ? styles.lowStockRow : {}}
            >
              <td>{item.name}</td>
              <td>{item.description || 'â€”'}</td>
              <td>{item.location}</td>
              <td>{item.quantity}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    background: '#f2f9ff',
    fontFamily: 'Arial',
    minHeight: '100vh'
  },
  summaryBox: {
    backgroundColor: '#ffffff',
    padding: '15px',
    marginBottom: '20px',
    borderRadius: '8px',
    boxShadow: '0 0 6px rgba(0,0,0,0.08)',
    fontWeight: 'bold'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    backgroundColor: '#fff',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)'
  },
  lowStockRow: {
    backgroundColor: '#fff0f0'
  }
};

export default InventorySummaryPage;
