import axios from 'axios';
import React, { useState } from 'react';

const API = 'http://localhost:5050/api/auth'; // <— IMPORTANT

export default function ForgotPasswordPage() {
  const [email, setEmail] = useState('');
  const [status, setStatus] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus(null);
    try {
      await axios.post(`${API}/forgot-password`, { email });
      setStatus({ ok: true, msg: 'If that email exists, a reset link has been sent.' });
    } catch (err) {
      const msg = err.response?.data?.message || 'Something went wrong. Please try again.';
      setStatus({ ok: false, msg });
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: 420, margin: '80px auto' }}>
      <input
        type="email"
        placeholder="you@example.com"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
        style={{ width: '100%', padding: 12, marginBottom: 12 }}
      />
      <button type="submit" style={{ width: '100%', padding: 12 }}>
        Send reset link
      </button>

      {status && (
        <p style={{ color: status.ok ? 'green' : 'red', marginTop: 12 }}>
          {status.msg}
        </p>
      )}

      <a href="/" style={{ display: 'inline-block', marginTop: 12 }}>Back to login</a>
    </form>
  );
}
