// src/pages/InventoryPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const InventoryPage = () => {
  const [items, setItems] = useState([]);
  const [mode, setMode] = useState('view');
  const [editItem, setEditItem] = useState(null);
  const [formData, setFormData] = useState({ name: '', description: '', location: '', quantity: '' });

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/inventory');
      setItems(res.data);
    } catch (err) {
      console.error('Error fetching inventory:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5050/api/inventory/delete/${id}`);
      fetchItems();
    } catch (err) {
      console.error('Error deleting item:', err);
    }
  };

  const handleEdit = (item) => {
    setEditItem(item);
    setFormData({
      name: item.name,
      description: item.description,
      location: item.location,
      quantity: item.quantity,
    });
  };

  const handleInputChange = (e) => {
    const { value, name } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleUpdate = async () => {
    try {
      await axios.put(`http://localhost:5050/api/inventory/update/${editItem._id}`, formData);
      fetchItems();
      setEditItem(null);
      setFormData({ name: '', description: '', location: '', quantity: '' });
    } catch (err) {
      console.error('Error updating item:', err);
    }
  };

  const handleModeToggle = (newMode) => {
    setMode(newMode);
    setEditItem(null);
    setFormData({ name: '', description: '', location: '', quantity: '' });
  };

  return (
    <div style={styles.page}>
      <h2 style={styles.heading}>ðŸ“¦ Inventory Items</h2>
      <div>
        <button onClick={() => handleModeToggle('view')} style={styles.button}>View Mode</button>
        <button onClick={() => handleModeToggle('update')} style={styles.button}>Update Mode</button>
      </div>

      {mode === 'update' && (
        <div style={{ margin: '20px 0' }}>
          <input
            type="text"
            name="name"
            placeholder="Item Name"
            value={formData.name}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="description"
            placeholder="Description"
            value={formData.description}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="location"
            placeholder="Location"
            value={formData.location}
            onChange={handleInputChange}
          />
          <input
            type="number"
            name="quantity"
            placeholder="Quantity"
            value={formData.quantity}
            onChange={handleInputChange}
          />
          {editItem ? (
            <>
              <button onClick={handleUpdate} style={styles.editBtn}>Update Item</button>
              <button onClick={() => setEditItem(null)} style={styles.deleteBtn}>Cancel</button>
            </>
          ) : (
            <></>
          )}
        </div>
      )}

      <table style={styles.table}>
        <thead>
          <tr>
            <th>Item Name</th>
            <th>Description</th>
            <th>Location</th>
            <th>Count</th>
            {mode === 'update' && <th>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {items.map((item) => (
            <tr key={item._id}>
              <td>{item.name}</td>
              <td>{item.description || 'â€”'}</td>
              <td>{item.location || 'â€”'}</td>
              <td>{item.quantity || 'â€”'}</td>
              {mode === 'update' && (
                <td>
                  <button onClick={() => handleEdit(item)} style={styles.editBtn}>Edit</button>
                  <button onClick={() => handleDelete(item._id)} style={styles.deleteBtn}>Delete</button>
                </td>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const styles = {
  page: {
    padding: '30px',
    background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
    minHeight: '100vh',
    color: '#fff',
  },
  heading: {
    marginBottom: '20px'
  },
  button: {
    padding: '8px 14px',
    marginRight: '10px',
    backgroundColor: '#fff',
    color: '#000',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    backgroundColor: '#fff',
    color: '#000',
    borderRadius: '8px',
    overflow: 'hidden'
  },
  editBtn: {
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    padding: '6px 10px',
    borderRadius: '4px',
    marginRight: '5px',
    cursor: 'pointer'
  },
  deleteBtn: {
    backgroundColor: '#dc3545',
    color: '#fff',
    border: 'none',
    padding: '6px 10px',
    borderRadius: '4px',
    cursor: 'pointer'
  }
};

export default InventoryPage;
