import React from 'react';
import { useNavigate } from 'react-router-dom';

function StembassadorDashboard() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));

  return (
    <div
      style={{
        minHeight: '100vh',
        backgroundImage: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
        padding: '2rem',
      }}
    >
      <div className="bg-white rounded-xl shadow-md p-6 mb-6">
        <h1 className="text-2xl font-bold mb-2">Welcome, {user?.username}!</h1>
        <p className="text-lg mb-4">Role: stembassador</p>
        <button
          className="bg-red-500 text-white px-4 py-2 rounded"
          onClick={() => {
            localStorage.removeItem('user');
            navigate('/');
          }}
        >
          Logout
        </button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div className="bg-white rounded-xl shadow-md p-6">
          <h3 className="text-xl font-bold mb-2">üì¶ View Inventory</h3>
          <p className="mb-4">Browse and manage items in stock.</p>
          <button
            onClick={() => navigate('/inventory')}
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            Go
          </button>
        </div>

        <div className="bg-white rounded-xl shadow-md p-6">
          <h3 className="text-xl font-bold mb-2">üìù Request Items</h3>
          <p className="mb-4">Request items for events or programs.</p>
          <button
            onClick={() => navigate('/stembassador/request')}
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            Go
          </button>
        </div>
      </div>
    </div>
  );
}

export default StembassadorDashboard;
