import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { saveAs } from 'file-saver';

const FulfillRequestsPage = () => {
  const [requests, setRequests] = useState([]);
  const [message, setMessage] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchRequests();
  }, []);

  const fetchRequests = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/requests/pending');
      setRequests(res.data);
    } catch (err) {
      console.error('Error fetching requests:', err);
    }
  };

  const handleAction = async (id, action) => {
    try {
      await axios.put(`http://localhost:5050/api/requests/${action}/${id}`);
      setMessage(`Request ${action}ed successfully.`);
      fetchRequests();
    } catch (err) {
      console.error(`Error trying to ${action} request:`, err);
      setMessage(`Failed to ${action} request.`);
    }
  };

  const handleExport = () => {
    const fulfilled = requests.filter(r => r.status === 'approved' || r.status === 'rejected');
    const csvContent = `Item,Quantity,Requested By,Status\n` +
      fulfilled.map(req => `${req.itemName || req.itemId?.name},${req.quantity},${req.requestedBy},${req.status}`).join('\n');
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    saveAs(blob, 'fulfilled_requests.csv');
  };

  const filteredRequests = requests.filter(req => {
    const itemName = req.itemName || req.itemId?.name || '';
    return itemName.toLowerCase().includes(searchTerm.toLowerCase()) ||
           req.requestedBy?.toLowerCase().includes(searchTerm.toLowerCase());
  });

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>ðŸ“¬ Fulfill Requests</h2>

      {message && <p style={styles.message}>{message}</p>}

      <input
        type="text"
        placeholder="Search by item or user..."
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
        style={styles.searchBar}
      />

      <button onClick={handleExport} style={styles.exportBtn}>Export</button>

      {filteredRequests.length === 0 ? (
        <p>No pending requests found.</p>
      ) : (
        <table style={styles.table}>
          <thead>
            <tr>
              <th>Item</th>
              <th>Quantity</th>
              <th>Requested By</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredRequests.map((req) => (
              <tr key={req._id}>
                <td>{req.itemName || req.itemId?.name || 'Unknown'}</td>
                <td>{req.quantity}</td>
                <td>{req.requestedBy}</td>
                <td>
                  <button
                    onClick={() => handleAction(req._id, 'approve')}
                    style={styles.approveBtn}
                  >
                    Approve
                  </button>
                  <button
                    onClick={() => handleAction(req._id, 'reject')}
                    style={styles.rejectBtn}
                  >
                    Reject
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    fontFamily: 'Arial, sans-serif',
    background: 'linear-gradient(to right, #1e3c52, #274960)',
    minHeight: '100vh',
    color: 'white'
  },
  heading: {
    color: 'white',
  },
  message: {
    padding: '10px',
    marginBottom: '15px',
    backgroundColor: '#cce5ff',
    color: '#004085',
    border: '1px solid #b8daff',
    borderRadius: '5px',
    fontWeight: 'bold'
  },
  searchBar: {
    padding: '8px',
    marginBottom: '15px',
    width: '320px',
    borderRadius: '4px',
    border: '1px solid #ccc',
    fontSize: '14px'
  },
  exportBtn: {
    marginLeft: '10px',
    padding: '8px 16px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    backgroundColor: 'white',
    color: 'black',
    borderRadius: '8px',
    overflow: 'hidden',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)'
  },
  approveBtn: {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '6px 12px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    marginRight: '5px'
  },
  rejectBtn: {
    backgroundColor: '#dc3545',
    color: 'white',
    padding: '6px 12px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer'
  }
};

export default FulfillRequestsPage;
