import React, { useEffect, useState } from 'react';
import axios from 'axios';

function InventoryPage() {
  const [inventory, setInventory] = useState([]);
  const [isUpdateMode, setIsUpdateMode] = useState(false);
  const [showAddForm, setShowAddForm] = useState(false);
  const [newItem, setNewItem] = useState({ name: '', description: '', location: '', count: '' });

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    try {
      const response = await axios.get('/api/inventory');
      setInventory(response.data);
    } catch (error) {
      console.error('Error fetching inventory:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewItem({ ...newItem, [name]: value });
  };

  const handleAddItem = async () => {
    try {
      await axios.post('/api/inventory', newItem);
      fetchInventory();
      setNewItem({ name: '', description: '', location: '', count: '' });
      setShowAddForm(false);
    } catch (error) {
      console.error('Error adding item:', error);
    }
  };

  const groupByRack = (items) => {
    const grouped = {};
    items.forEach(item => {
      const rack = item.location || 'Unknown Rack';
      if (!grouped[rack]) grouped[rack] = [];
      grouped[rack].push(item);
    });
    return grouped;
  };

  const groupedInventory = groupByRack(inventory);

  return (
    <div style={{ padding: '20px', background: 'linear-gradient(to right, #1f3c47, #274760)', minHeight: '100vh' }}>
      <h1 style={{ color: 'white', display: 'flex', alignItems: 'center' }}>
        <span role="img" aria-label="box" style={{ marginRight: '10px' }}>📦</span>
        Inventory Items
      </h1>

      <div style={{ marginBottom: '20px' }}>
        <button onClick={() => { setIsUpdateMode(false); setShowAddForm(false); }} style={{ marginRight: '10px' }}>View Mode</button>
        <button onClick={() => { setIsUpdateMode(true); setShowAddForm(false); }}>Update Mode</button>
      </div>

      {isUpdateMode && (
        <button onClick={() => setShowAddForm(true)} style={{ marginBottom: '20px', backgroundColor: 'green', color: 'white', padding: '10px', border: 'none', borderRadius: '5px' }}>
          + Add New Item
        </button>
      )}

      {showAddForm && (
        <div style={{ backgroundColor: 'white', padding: '20px', borderRadius: '10px', width: '400px', margin: 'auto', marginBottom: '30px' }}>
          <h3>Add New Item</h3>
          <input name="name" placeholder="Item Name" value={newItem.name} onChange={handleInputChange} style={{ width: '100%', marginBottom: '10px' }} />
          <input name="description" placeholder="Description" value={newItem.description} onChange={handleInputChange} style={{ width: '100%', marginBottom: '10px' }} />
          <input name="location" placeholder="Location" value={newItem.location} onChange={handleInputChange} style={{ width: '100%', marginBottom: '10px' }} />
          <input name="count" placeholder="Count" type="number" value={newItem.count} onChange={handleInputChange} style={{ width: '100%', marginBottom: '10px' }} />
          <button onClick={handleAddItem} style={{ marginRight: '10px' }}>Add</button>
          <button onClick={() => setShowAddForm(false)} style={{ backgroundColor: 'red', color: 'white' }}>Cancel</button>
        </div>
      )}

      {!showAddForm && Object.keys(groupedInventory).map((rack) => (
        <div key={rack} style={{ backgroundColor: 'white', padding: '20px', borderRadius: '10px', marginBottom: '20px' }}>
          <h3><span role="img" aria-label="pin">📍</span> Rack {rack}</h3>
          <table style={{ width: '100%', borderCollapse: 'collapse', textAlign: 'left' }}>
            <thead>
              <tr>
                <th style={{ padding: '10px' }}>Item Name</th>
                <th style={{ padding: '10px' }}>Description</th>
                <th style={{ padding: '10px' }}>Count</th>
              </tr>
            </thead>
            <tbody>
              {groupedInventory[rack].map((item) => (
                <tr key={item._id}>
                  <td style={{ padding: '10px' }}>{item.name}</td>
                  <td style={{ padding: '10px' }}>{item.description || '—'}</td>
                  <td style={{ padding: '10px' }}>{item.count}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
}

export default InventoryPage;
