import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RequestPage = () => {
  const [items, setItems] = useState([]);
  const [selectedItem, setSelectedItem] = useState('');
  const [quantity, setQuantity] = useState('');
  const [note, setNote] = useState('');
  const [message, setMessage] = useState('');

  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchItems = async () => {
      try {
        const res = await axios.get('http://localhost:5050/api/inventory');
        setItems(res.data);
      } catch (err) {
        console.error('Failed to fetch items:', err);
      }
    };
    fetchItems();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedItem || !quantity) {
      setMessage('Please select an item and enter quantity.');
      return;
    }

    try {
      const res = await axios.post('http://localhost:5050/api/requests/add', {
        itemName: selectedItem,
        quantity,
        note,
        requestedBy: user?.username || 'Unknown'
      });

      setMessage('Request submitted successfully!');
      setSelectedItem('');
      setQuantity('');
      setNote('');
    } catch (err) {
      console.error(err);
      setMessage('Failed to submit request.');
    }
  };

  return (
    <div style={styles.container}>
      <h2>üìù Request Items</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        <label style={styles.label}>Select Item:</label>
        <select
          value={selectedItem}
          onChange={(e) => setSelectedItem(e.target.value)}
          style={styles.input}
          required
        >
          <option value="">-- Choose an item --</option>
          {items.map((item) => (
            <option key={item._id} value={item.name}>
              {item.name}
            </option>
          ))}
        </select>

        <label style={styles.label}>Quantity:</label>
        <input
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          style={styles.input}
          min="1"
          required
        />

        <label style={styles.label}>Note (optional):</label>
        <textarea
          value={note}
          onChange={(e) => setNote(e.target.value)}
          style={styles.input}
          rows={3}
        />

        <button type="submit" style={styles.button}>Submit Request</button>

        {message && <p style={styles.message}>{message}</p>}
      </form>
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    background: '#f0fff4',
    minHeight: '100vh',
    fontFamily: 'Arial'
  },
  form: {
    backgroundColor: 'white',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)',
    maxWidth: '400px',
    marginTop: '20px'
  },
  label: {
    display: 'block',
    marginBottom: '6px',
    fontWeight: 'bold',
    marginTop: '10px'
  },
  input: {
    width: '100%',
    padding: '8px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    marginBottom: '10px',
    fontSize: '14px'
  },
  button: {
    width: '100%',
    padding: '10px',
    backgroundColor: '#28a745',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    fontWeight: 'bold',
    cursor: 'pointer'
  },
  message: {
    marginTop: '10px',
    fontWeight: 'bold'
  }
};

export default RequestPage;
