import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RequestItemsPage = () => {
  const [inventory, setInventory] = useState([]);
  const [selectedItem, setSelectedItem] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [requests, setRequests] = useState([]);
  const [message, setMessage] = useState('');

  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchInventory = async () => {
      try {
        const res = await axios.get('http://localhost:5050/api/inventory');
        setInventory(res.data);
      } catch (err) {
        console.error('Error fetching inventory:', err);
      }
    };
    fetchInventory();
  }, []);

  const addRequest = () => {
    if (!selectedItem || quantity < 1) return;
    const item = inventory.find(i => i._id === selectedItem);
    setRequests(prev => [...prev, { itemId: selectedItem, name: item.name, quantity }]);
    setQuantity(1);
    setSelectedItem('');
  };

  const submitRequests = async () => {
    try {
      await axios.post('http://localhost:5050/api/requests', {
        requests: requests.map(({ itemId, quantity }) => ({ itemId, quantity })),
        requestedBy: user.username,
      });
      setMessage('✅ Request submitted successfully!');
      setRequests([]);
    } catch (err) {
      console.error('Error submitting request:', err);
      setMessage('❌ Failed to submit request.');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 to-blue-700 text-white px-6 py-12 font-sans">
      <div className="max-w-3xl mx-auto bg-white text-black rounded-xl shadow-xl p-8">
        <h1 className="text-3xl font-bold mb-4 text-center text-blue-900">📚 Request Inventory Items</h1>
        <p className="text-center text-gray-600 mb-6">Submit requests for the items you need below.</p>

        <div className="flex flex-col md:flex-row gap-4 mb-6 items-center">
          <select
            value={selectedItem}
            onChange={e => setSelectedItem(e.target.value)}
            className="w-full md:w-1/2 p-2 border border-gray-300 rounded"
          >
            <option value="">Select Item</option>
            {inventory.map(item => (
              <option key={item._id} value={item._id}>
                {item.name} (Available: {item.count})
              </option>
            ))}
          </select>

          <input
            type="number"
            value={quantity}
            min="1"
            onChange={e => setQuantity(Number(e.target.value))}
            className="w-full md:w-1/4 p-2 border border-gray-300 rounded"
          />

          <button
            onClick={addRequest}
            className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded font-semibold"
          >
            Add
          </button>
        </div>

        {requests.length > 0 && (
          <div className="bg-gray-50 border border-gray-300 rounded-lg p-4 mb-6">
            <h2 className="font-semibold mb-2">📝 Items to Request:</h2>
            <ul className="list-disc pl-5 text-gray-800">
              {requests.map((r, idx) => (
                <li key={idx}>
                  {r.name} — Quantity: {r.quantity}
                </li>
              ))}
            </ul>
          </div>
        )}

        <div className="text-center">
          <button
            onClick={submitRequests}
            disabled={requests.length === 0}
            className={`w-full md:w-1/2 px-6 py-3 rounded font-bold transition ${
              requests.length === 0
                ? 'bg-gray-400 cursor-not-allowed'
                : 'bg-blue-600 hover:bg-blue-700 text-white'
            }`}
          >
            Submit Request
          </button>
        </div>

        {message && <p className="mt-4 text-center text-blue-800 font-medium">{message}</p>}
      </div>
    </div>
  );
};

export default RequestItemsPage;
