import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ApprovePage = () => {
  const [requests, setRequests] = useState([]);
  const [updatingId, setUpdatingId] = useState(null);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchRequests();
  }, []);

  const fetchRequests = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/requests');
      setRequests(res.data);
    } catch (err) {
      console.error('Failed to fetch requests:', err);
    }
  };

  const updateStatus = async (id, newStatus) => {
    try {
      setUpdatingId(id);
      await axios.put(`http://localhost:5050/api/requests/${id}`, { status: newStatus });
      setMessage(`Request ${newStatus.toLowerCase()} successfully.`);
      fetchRequests(); // Refresh the list
    } catch (err) {
      console.error('Failed to update request:', err);
      setMessage('Something went wrong.');
    } finally {
      setUpdatingId(null);
    }
  };

  return (
    <div style={styles.container}>
      <h2>âœ… Review Item Requests</h2>

<div style={styles.summary}>
  <p>ðŸ“Š <strong>Request Summary</strong></p>
  <p>
    Total: {requests.length} |{' '}
    Approved: {requests.filter(r => r.status === 'Approved').length} |{' '}
    Pending: {requests.filter(r => r.status === 'Pending').length} |{' '}
    Rejected: {requests.filter(r => r.status === 'Rejected').length}
  </p>
</div>
      {message && <p style={styles.message}>{message}</p>}

      {requests.length === 0 ? (
        <p>No requests found.</p>
      ) : (
        <table style={styles.table}>
          <thead>
            <tr>
              <th>Item</th>
              <th>Quantity</th>
              <th>Note</th>
              <th>Requested By</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {requests.map((req) => (
              <tr key={req._id}>
                <td>{req.itemName}</td>
                <td>{req.quantity}</td>
                <td>{req.note || 'â€”'}</td>
                <td>{req.requestedBy}</td>
                <td>
                  <span style={{ 
                    fontWeight: 'bold', 
                    color: req.status === 'Approved' ? 'green' : req.status === 'Rejected' ? 'red' : '#888' 
                  }}>
                    {req.status}
                  </span>
                </td>
                <td>
                  {req.status === 'Pending' ? (
                    <>
                      <button
                        style={{ ...styles.btn, backgroundColor: '#28a745' }}
                        onClick={() => updateStatus(req._id, 'Approved')}
                        disabled={updatingId === req._id}
                      >
                        Approve
                      </button>
                      <button
                        style={{ ...styles.btn, backgroundColor: '#dc3545' }}
                        onClick={() => updateStatus(req._id, 'Rejected')}
                        disabled={updatingId === req._id}
                      >
                        Reject
                      </button>
                    </>
                  ) : (
                    <em>â€”</em>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    fontFamily: 'Arial, sans-serif',
    background: 'linear-gradient(to right, #fdfbfb, #ebedee)',
    minHeight: '100vh'
  },
  message: {
    backgroundColor: '#e0f7e9',
    padding: '10px',
    borderRadius: '5px',
    color: '#155724',
    fontWeight: 'bold',
    marginBottom: '10px',
    border: '1px solid #c3e6cb'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    marginTop: '20px',
    backgroundColor: '#fff',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)'
  },
  btn: {
    color: 'white',
    border: 'none',
    padding: '6px 10px',
    marginRight: '5px',
    borderRadius: '4px',
    cursor: 'pointer'
  }
};

export default ApprovePage;
