// src/pages/InventoryPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import AddInventoryItemForm from '../components/AddInventoryItemForm';

const InventoryPage = () => {
  const [items, setItems] = useState([]);
  const [mode, setMode] = useState('view');
  const [filterRack, setFilterRack] = useState('All');

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/inventory');
      setItems(res.data);
    } catch (err) {
      console.error('Error fetching inventory:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5050/api/inventory/delete/${id}`);
      fetchItems();
    } catch (err) {
      console.error('Error deleting item:', err);
    }
  };

  const handleModeToggle = (newMode) => {
    setMode(newMode);
  };

  const handleFilterChange = (e) => {
    setFilterRack(e.target.value);
  };

  const groupByRack = (data) => {
    const grouped = {};
    data.forEach((item) => {
      const rack = item.location || 'Unknown';
      if (!grouped[rack]) grouped[rack] = [];
      grouped[rack].push(item);
    });
    return grouped;
  };

  const filteredItems = filterRack === 'All' ? items : items.filter(item => item.location === filterRack);
  const groupedItems = groupByRack(filteredItems);

  return (
    <div style={styles.page}>
      <h2 style={styles.heading}><span role="img" aria-label="box">üì¶</span> Inventory Items</h2>
      <div style={{ marginBottom: '15px' }}>
        <button onClick={() => handleModeToggle('view')} style={styles.button}>View Mode</button>
        <button onClick={() => handleModeToggle('update')} style={styles.button}>Update Mode</button>
      </div>

      {mode === 'view' && (
        <>
          <label style={{ color: 'white', marginRight: 10 }}>Filter by Rack:</label>
          <select onChange={handleFilterChange} value={filterRack} style={styles.dropdown}>
            <option value="All">All</option>
            {Array.from(new Set(items.map(item => item.location))).map(loc => (
              <option key={loc} value={loc}>{loc}</option>
            ))}
          </select>
          {Object.keys(groupedItems).sort().map((rack) => (
            <div key={rack} style={styles.rackSection}>
              <h3>üìç Rack {rack}</h3>
              <table style={styles.table}>
                <thead>
                  <tr>
                    <th>Item Name</th>
                    <th>Description</th>
                    <th>Count</th>
                  </tr>
                </thead>
                <tbody>
                  {groupedItems[rack].map((item) => (
                    <tr key={item._id}>
                      <td>{item.name}</td>
                      <td>{item.description || '‚Äî'}</td>
                      <td>{item.quantity || '‚Äî'}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </>
      )}

      {mode === 'update' && (
        <>
          <AddInventoryItemForm onAdd={fetchItems} />
          <table style={styles.table}>
            <thead>
              <tr>
                <th>Item Name</th>
                <th>Description</th>
                <th>Location</th>
                <th>Count</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {items.map((item) => (
                <tr key={item._id}>
                  <td>{item.name}</td>
                  <td>{item.description || '‚Äî'}</td>
                  <td>{item.location || '‚Äî'}</td>
                  <td>{item.quantity || '‚Äî'}</td>
                  <td>
                    <button style={styles.editBtn}>Edit</button>
                    <button onClick={() => handleDelete(item._id)} style={styles.deleteBtn}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
};

const styles = {
  page: {
    padding: '30px',
    background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
    minHeight: '100vh',
    color: '#fff',
  },
  heading: {
    marginBottom: '20px'
  },
  button: {
    padding: '8px 14px',
    marginRight: '10px',
    backgroundColor: '#fff',
    color: '#000',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer'
  },
  dropdown: {
    padding: '6px 12px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    marginBottom: '20px'
  },
  rackSection: {
    backgroundColor: '#f9f9f9',
    color: '#000',
    borderRadius: '10px',
    padding: '20px',
    marginBottom: '20px'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    backgroundColor: '#fff',
    color: '#000',
    borderRadius: '8px',
    overflow: 'hidden'
  },
  editBtn: {
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    padding: '6px 10px',
    borderRadius: '4px',
    marginRight: '5px',
    cursor: 'pointer'
  },
  deleteBtn: {
    backgroundColor: '#dc3545',
    color: '#fff',
    border: 'none',
    padding: '6px 10px',
    borderRadius: '4px',
    cursor: 'pointer'
  }
};

export default InventoryPage;
