import React, { useEffect, useState } from 'react';
import axios from 'axios';

function InventoryPage() {
  const [items, setItems] = useState([]);
  const [isUpdateMode, setIsUpdateMode] = useState(false);
  const [showAddForm, setShowAddForm] = useState(false);
  const [newItem, setNewItem] = useState({ itemName: '', description: '', location: '', count: '' });

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      const response = await axios.get('/api/inventory');
      setItems(response.data);
    } catch (error) {
      console.error('Error fetching inventory items:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewItem((prev) => ({ ...prev, [name]: value }));
  };

  const handleAddItem = async () => {
    try {
      await axios.post('/api/inventory', newItem);
      fetchItems();
      setShowAddForm(false);
      setNewItem({ itemName: '', description: '', location: '', count: '' });
    } catch (error) {
      console.error('Error adding item:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/inventory/${id}`);
      fetchItems();
    } catch (error) {
      console.error('Error deleting item:', error);
    }
  };

  const groupedItems = items.reduce((acc, item) => {
    const rack = item.location || 'Unknown Rack';
    if (!acc[rack]) acc[rack] = [];
    acc[rack].push(item);
    return acc;
  }, {});

  const tableHeaderStyle = {
    borderBottom: '2px solid #ccc',
    textAlign: 'left',
    padding: '8px',
    fontWeight: 'bold'
  };

  const tableCellStyle = {
    borderBottom: '1px solid #eee',
    textAlign: 'left',
    padding: '8px'
  };

  return (
    <div style={{ padding: '2rem', background: 'linear-gradient(to right, #1f3c4c, #1f4c4c)', minHeight: '100vh' }}>
      <h2 style={{ color: 'white', marginBottom: '1rem' }}>üì¶ Inventory Items</h2>
      <div style={{ marginBottom: '1rem' }}>
        <button onClick={() => { setIsUpdateMode(false); setShowAddForm(false); }} className="btn btn-primary" style={{ marginRight: '10px' }}>View Mode</button>
        <button onClick={() => setIsUpdateMode(true)} className="btn btn-primary">Update Mode</button>
      </div>

      {isUpdateMode && (
        <div>
          {!showAddForm && <button onClick={() => setShowAddForm(true)} className="btn btn-success" style={{ marginBottom: '1rem' }}>+ Add New Item</button>}
          {showAddForm && (
            <div style={{ marginBottom: '2rem', background: 'white', padding: '1rem', borderRadius: '10px', maxWidth: '400px', marginInline: 'auto' }}>
              <h5>Add New Item</h5>
              <input name="itemName" value={newItem.itemName} onChange={handleInputChange} placeholder="Item Name" className="form-control mb-2" />
              <input name="description" value={newItem.description} onChange={handleInputChange} placeholder="Description" className="form-control mb-2" />
              <input name="location" value={newItem.location} onChange={handleInputChange} placeholder="Location" className="form-control mb-2" />
              <input name="count" value={newItem.count} onChange={handleInputChange} placeholder="Count" className="form-control mb-2" />
              <button onClick={handleAddItem} className="btn btn-primary mr-2">Add</button>
              <button onClick={() => setShowAddForm(false)} className="btn btn-danger">Cancel</button>
            </div>
          )}
          <table className="table table-striped table-bordered">
            <thead className="thead-dark">
              <tr>
                <th>Item Name</th>
                <th>Description</th>
                <th>Location</th>
                <th>Count</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {items.map((item) => (
                <tr key={item._id}>
                  <td>{item.itemName}</td>
                  <td>{item.description || '‚Äî'}</td>
                  <td>{item.location || '‚Äî'}</td>
                  <td>{item.count}</td>
                  <td>
                    <button className="btn btn-sm btn-primary mr-2">Edit</button>
                    <button onClick={() => handleDelete(item._id)} className="btn btn-sm btn-danger">Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {!isUpdateMode && (
        <div>
          {Object.entries(groupedItems).map(([rack, items]) => (
            <div key={rack} style={{ backgroundColor: 'white', padding: '1rem', marginBottom: '1.5rem', borderRadius: '10px' }}>
              <h5 style={{ marginBottom: '1rem' }}>üìç Rack {rack}</h5>
              <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                  <tr>
                    <th style={tableHeaderStyle}>Item Name</th>
                    <th style={tableHeaderStyle}>Description</th>
                    <th style={tableHeaderStyle}>Count</th>
                  </tr>
                </thead>
                <tbody>
                  {items.map(item => (
                    <tr key={item._id}>
                      <td style={tableCellStyle}>{item.itemName}</td>
                      <td style={tableCellStyle}>{item.description || '‚Äî'}</td>
                      <td style={tableCellStyle}>{item.count}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default InventoryPage;
