import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RequestItemsPage = () => {
  const [inventory, setInventory] = useState([]);
  const [selectedItem, setSelectedItem] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [requests, setRequests] = useState([]);
  const [message, setMessage] = useState('');
  const [history, setHistory] = useState([]);

  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchInventory = async () => {
      try {
        const res = await axios.get('http://localhost:5050/api/inventory');
        setInventory(res.data);
      } catch (err) {
        console.error('Error fetching inventory:', err);
      }
    };

    const fetchHistory = async () => {
      try {
        const res = await axios.get('http://localhost:5050/api/requests/user/' + user.username);
        setHistory(res.data);
      } catch (err) {
        console.error('Error fetching request history:', err);
      }
    };

    fetchInventory();
    fetchHistory();
  }, [user.username]);

  const addRequest = () => {
    if (!selectedItem || quantity < 1) return;
    const item = inventory.find(i => i._id === selectedItem);
    setRequests(prev => [...prev, { itemId: selectedItem, name: item.name, quantity }]);
    setQuantity(1);
    setSelectedItem('');
  };

  const submitRequests = async () => {
    try {
      await axios.post('http://localhost:5050/api/requests', {
        requests: requests.map(({ itemId, quantity }) => ({ itemId, quantity })),
        requestedBy: user.username,
      });
      setMessage('✅ Request submitted successfully!');
      setRequests([]);
    } catch (err) {
      console.error('Error submitting request:', err);
      setMessage('❌ Failed to submit request.');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-900 to-blue-700 text-white py-12 px-4">
      <div className="max-w-3xl mx-auto">
        <h1 className="text-4xl font-bold mb-8 flex items-center gap-2 justify-center">
          📚 Request Inventory Items
        </h1>

        <div className="bg-white text-black rounded-lg shadow-lg p-6 mb-6">
          <div className="flex flex-col md:flex-row gap-4">
            <select
              value={selectedItem}
              onChange={e => setSelectedItem(e.target.value)}
              className="p-2 border rounded w-full md:w-1/2"
            >
              <option value="">Select Item</option>
              {inventory.map(item => (
                <option key={item._id} value={item._id}>
                  {item.name} (Available: {item.count})
                </option>
              ))}
            </select>

            <input
              type="number"
              value={quantity}
              min="1"
              onChange={e => setQuantity(Number(e.target.value))}
              className="p-2 border rounded w-full md:w-1/4"
            />

            <button
              onClick={addRequest}
              className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 w-full md:w-auto"
            >
              ➕ Add
            </button>
          </div>
        </div>

        {requests.length > 0 && (
          <div className="bg-white text-black rounded-lg shadow-lg p-6 mb-6">
            <h2 className="text-xl font-semibold mb-3">📝 Items to Request:</h2>
            <ul className="list-disc pl-6 space-y-1">
              {requests.map((r, idx) => (
                <li key={idx}>
                  {r.name} — Quantity: {r.quantity}
                </li>
              ))}
            </ul>
          </div>
        )}

        <button
          onClick={submitRequests}
          disabled={requests.length === 0}
          className={`w-full px-6 py-2 rounded font-semibold transition duration-300 ${
            requests.length === 0
              ? 'bg-gray-400 cursor-not-allowed'
              : 'bg-blue-600 hover:bg-blue-700'
          }`}
        >
          Submit Request
        </button>

        {message && <p className="mt-4 text-yellow-200 font-medium text-center">{message}</p>}

        {/* Order History */}
        <div className="mt-10 bg-white text-black rounded-lg shadow-lg p-6">
          <h2 className="text-xl font-bold mb-4">📜 Your Previous Requests</h2>
          {history.length === 0 ? (
            <p className="text-gray-600">No request history available.</p>
          ) : (
            <ul className="space-y-2">
              {history.map((req, idx) => (
                <li key={idx} className="border-b pb-2">
                  <strong>{req.itemId?.name || 'Item'}:</strong> {req.quantity} pcs —{' '}
                  <span
                    className={`font-semibold ${
                      req.status === 'approved'
                        ? 'text-green-600'
                        : req.status === 'rejected'
                        ? 'text-red-600'
                        : 'text-yellow-600'
                    }`}
                  >
                    {req.status}
                  </span>{' '}
                  <span className="text-sm text-gray-500">
                    ({new Date(req.requestedAt).toLocaleDateString()})
                  </span>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
};

export default RequestItemsPage;
