// src/pages/ForgotPasswordPage.js
import React, { useState } from 'react';
import axios from 'axios';

const API = 'http://localhost:5050/api/auth';

export default function ForgotPasswordPage() {
  const [email, setEmail] = useState('');
  const [status, setStatus] = useState({ type: '', msg: '' });
  const [loading, setLoading] = useState(false);

  const submit = async (e) => {
    e.preventDefault();
    setStatus({ type: '', msg: '' });
    setLoading(true);
    try {
      await axios.post(`${API}/forgot-password`, { email });
      setStatus({
        type: 'ok',
        msg: 'If that email exists, a reset link has been sent.',
      });
      setEmail('');
    } catch (err) {
      setStatus({ type: 'err', msg: 'Something went wrong. Please try again.' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={wrap}>
      <h2>Forgot Password</h2>
      <p>Enter the email associated with your account. We’ll send you a reset link.</p>
      <form onSubmit={submit} style={form}>
        <input
          type="email"
          placeholder="you@example.org"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          style={input}
        />
        <button type="submit" style={btn} disabled={loading}>
          {loading ? 'Sending…' : 'Send reset link'}
        </button>
      </form>
      {status.msg && (
        <p style={{ color: status.type === 'ok' ? '#2e7d32' : '#c62828' }}>{status.msg}</p>
      )}
      <a href="/" style={{ display: 'inline-block', marginTop: 8 }}>Back to login</a>
    </div>
  );
}

const wrap = { padding: 24, maxWidth: 420, margin: '40px auto', color: '#fff' };
const form = { display: 'flex', gap: 8, flexDirection: 'column' };
const input = { padding: 12, borderRadius: 8, border: '1px solid #ccc', color: '#000' };
const btn = { padding: 12, borderRadius: 8, border: 'none', cursor: 'pointer' };
