// src/pages/Dashboard.js
import React from 'react';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const user = JSON.parse(localStorage.getItem('user'));
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/');
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h2>Welcome, {user?.username}!</h2>
        <p>Role: {user?.role}</p>
        <button onClick={handleLogout} style={styles.logoutBtn}>Logout</button>
      </div>

      {user?.role === 'inventorymanager' && (
        <div style={styles.card}>
          <h3>üõ†Ô∏è Manage Inventory</h3>
          <p>Add, edit, or delete stock items.</p>
          <button onClick={() => navigate('/inventory')} style={styles.goBtn}>Go</button>
        </div>
      )}

      {user?.role === 'stembassador' && (
        <>
          <div style={styles.card}>
            <h3>üì¶ View Inventory</h3>
            <p>Browse and manage items in stock.</p>
            <button onClick={() => navigate('/inventory')} style={styles.goBtn}>Go</button>
          </div>
          <div style={styles.card}>
            <h3>üìù Request Items</h3>
            <p>Request items for events or programs.</p>
            <button onClick={() => navigate('/request')} style={styles.goBtn}>Go</button>
          </div>
        </>
      )}

      {user?.role === 'director' && (
        <div style={styles.card}>
          <h3>‚úÖ Approve Requests</h3>
          <p>Review and approve item requests.</p>
          <button onClick={() => navigate('/approve')} style={styles.goBtn}>Go</button>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    fontFamily: 'Arial, sans-serif',
    background: 'linear-gradient(to right, #d4fc79, #96e6a1)',
    minHeight: '100vh'
  },
  card: {
    background: '#fff',
    padding: '20px',
    marginBottom: '20px',
    borderRadius: '12px',
    boxShadow: '0px 2px 10px rgba(0,0,0,0.1)'
  },
  logoutBtn: {
    marginTop: '10px',
    padding: '8px 14px',
    borderRadius: '6px',
    border: 'none',
    backgroundColor: '#dc3545',
    color: 'white',
    cursor: 'pointer'
  },
  goBtn: {
    marginTop: '10px',
    padding: '8px 14px',
    borderRadius: '6px',
    border: 'none',
    backgroundColor: '#007bff',
    color: 'white',
    cursor: 'pointer'
  }
};

export default Dashboard;
